pragma solidity 0.8.26;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {EIP20NonStandardInterface} from "./EIP20NonStandardInterface.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {TokenTransfersLibrary} from "./TokenTransfersLibrary.sol";

abstract contract TokenSaleRegisterStorage is OwnableUpgradeable {
    /// @dev the timestamp at which the token sale will begin
    uint256 public saleStart;
    /// @dev the timestamp at which the token sale will end
    uint256 public saleEnd;
    /// @dev the sale price in WAD
    uint256 public salePrice;
    /// @dev the maximum amount of the PMX that can be sold
    uint256 public maxSaleAmount;
    /// @dev How many tokens have been sold so far
    uint256 public totalSold;

    mapping(address user => uint256 balance) public userBalances;
    mapping(address token => bool acceptable) public allowedTokens;
    mapping(address token => uint256 multiplier) public tokenMultiplier;
    address[] public buyers;
}

contract TokenSaleRegister is TokenSaleRegisterStorage {
    event Buying(address indexed buyer, uint256 amount);
    event RateChanged(uint256 rate);

    uint256 constant WAD = 10 ** 18;

    constructor() {
        _disableInitializers();
    }

    /**
     * @notice  Initializes the TokenSaleRegister contract.
     * @dev This function should only be called once during the initial setup of the contract.
     * @param _saleStart the timestamp at which the token sale will begin
     * @param _saleEnd the timestamp at which the token sale will end
     * @param _salePrice the sale price in WAD
     * @param _maxSaleAmount the maximum amount of the PMX that can be sold
     * @param _allowedTokens Array of tokens that can be used to buy pmx
     */
    function initialize(
        uint256 _saleStart,
        uint256 _saleEnd,
        uint256 _salePrice,
        uint256 _maxSaleAmount,
        address[] calldata _allowedTokens
    ) external initializer {
        require(_salePrice > 0, "TokenSaleRegister: zero sale price");
        require(
            _saleStart >= block.timestamp,
            "TokenSaleRegister: incorrect token sale timestamps"
        );
        salePrice = _salePrice;
        maxSaleAmount = _maxSaleAmount;
        _setTimestamps(_saleStart, _saleEnd);
        for (uint256 i; i < _allowedTokens.length; i++) {
            allowedTokens[_allowedTokens[i]] = true;
            tokenMultiplier[_allowedTokens[i]] =
                10 ** (18 - IERC20Metadata(_allowedTokens[i]).decimals());
        }
        __Ownable_init();
    }

    /**
     * @notice Sets the new sale rate
     * Only callable by the owner
     * @param _salePrice The new sale rate in WAD format to be set.
     */

    function changeSalePrice(uint256 _salePrice) external onlyOwner {
        require(_salePrice > 0, "TokenSaleRegister: zero sale price");
        salePrice = _salePrice;
        emit RateChanged(_salePrice);
    }

    /**
     * @notice Sets the new sale timestamps
     * Only callable by the owner
     * @param _saleStart the timestamp at which the token sale will begin
     * @param _saleEnd the timestamp at which the token sale will end
     */
    function changeTimestamps(
        uint256 _saleStart,
        uint256 _saleEnd
    ) external onlyOwner {
        _setTimestamps(_saleStart, _saleEnd);
    }

    /**
     * @notice Sets the new max sale amount;
     * Only callable by the owner
     * @param _maxSaleAmount the maximum amount of the PMX that can be sold
     */
    function changeMaxSaleAmount(uint256 _maxSaleAmount) external onlyOwner {
        maxSaleAmount = _maxSaleAmount;
    }

    /**
     * @notice Adds the new allowed tokens
     * Only callable by the owner
     * @param _allowedTokens the array of allowed tokens
     */
    function addAllowedTokens(
        address[] calldata _allowedTokens
    ) external onlyOwner {
        for (uint256 i; i < _allowedTokens.length; i++) {
            allowedTokens[_allowedTokens[i]] = true;
            tokenMultiplier[_allowedTokens[i]] =
                10 ** (18 - IERC20Metadata(_allowedTokens[i]).decimals());
        }
    }
    /**
     * @notice Removes the tokens from the allowedTokens map
     * Only callable by the owner
     * @param _removedTokens the array of removed tokens
     */
    function removeAllowedTokens(
        address[] calldata _removedTokens
    ) external onlyOwner {
        for (uint256 i; i < _removedTokens.length; i++) {
            allowedTokens[_removedTokens[i]] = false;
        }
    }

    /**
     * @notice buys some pmx amount and writes it to the sender's balance
     * @param _investmentAmount the amount in usdt/usdc for which the pmx will be bought
     * @param _buyForToken For what token the pmx will be purchased
     */
    function buy(uint256 _investmentAmount, address _buyForToken) external {
        require(
            block.timestamp >= saleStart,
            "TokenSaleRegister: token sale has not started yet"
        );
        require(
            block.timestamp <= saleEnd,
            "TokenSaleRegister: token sale has already ended"
        );
        require(
            allowedTokens[_buyForToken],
            "TokenSaleRegister: token is not allowed"
        );
        uint256 adjInvestment = _investmentAmount *
            tokenMultiplier[_buyForToken];
        uint256 pmxAmount = (adjInvestment * WAD) / salePrice;
        require(
            totalSold + pmxAmount <= maxSaleAmount,
            "TokenSaleRegister: max sale amount reached"
        );
        require(pmxAmount > 0, "TokenSaleRegister: zero pmx amount");
        totalSold += pmxAmount;
        if (userBalances[msg.sender] == 0) buyers.push(msg.sender);
        userBalances[msg.sender] += pmxAmount;
        TokenTransfersLibrary.doTransferIn(
            _buyForToken,
            msg.sender,
            _investmentAmount
        );

        emit Buying(msg.sender, pmxAmount);
    }

    /**
     * @notice Used to recover non-PMX ERC20 tokens
     * @param _tokenAddr Address of the ERC20 token to retrieve
     * @param _receiver The address that will receive the retrieved tokens
     */
    function retrieveToken(
        address _tokenAddr,
        address _receiver
    ) external onlyOwner {
        TokenTransfersLibrary.doTransferOut(
            _tokenAddr,
            _receiver,
            IERC20(_tokenAddr).balanceOf(address(this))
        );
    }
    /**
     * @notice Sets the saleEnd to zero, thereby stopping the token sale
     */
    function completeTokenSale() external onlyOwner {
        saleEnd = 0;
    }

    function getBuyersLength() public view returns (uint256) {
        return buyers.length;
    }

    function getAllBuyersWithBalances(
        uint256 _cursor,
        uint256 _count
    )
        external
        view
        returns (
            address[] memory _buyers,
            uint256[] memory _balances,
            uint256 _newCursor
        )
    {
        uint256 buyersLength = getBuyersLength();
        if (_cursor >= buyersLength) {
            return (new address[](0), new uint256[](0), 0);
        }
        if (_cursor + _count >= buyersLength) {
            _count = buyersLength - _cursor;
        } else {
            _newCursor = _cursor + _count;
        }

        _buyers = new address[](_count);
        _balances = new uint256[](_count);
        for (uint256 i; i < _count; i++) {
            _buyers[i] = buyers[_cursor + i];
            _balances[i] = userBalances[_buyers[i]];
        }
        return (_buyers, _balances, _newCursor);
    }

    function _setTimestamps(uint256 _saleStart, uint256 _saleEnd) internal {
        require(
            _saleEnd > _saleStart,
            "TokenSaleRegister: incorrect token sale timestamps"
        );
        saleStart = _saleStart;
        saleEnd = _saleEnd;
    }
}
