    function _getAmountsWithCurve(GetAmountsParams memory _params) internal view returns (uint256[3] memory) {
        (address[] memory path, address[] memory pools) = abi.decode(_params.encodedPath, (address[], address[]));
        uint256 amountOut = _getExchangeAmountCurve(_params.amount, _params.dexRouter, path, pools);

        return [_params.amount, amountOut, 0];
    }

    function _getExchangeAmountCurve(
        uint256 amount,
        address dexRouter,
        address[] memory path,
        address[] memory pools
    ) internal view returns (uint256) {
        uint256 amountOut = amount;

        for (uint256 i; i < path.length - 1; i++) {
            amountOut = ICurveRouter(dexRouter).get_exchange_amount(pools[i], path[i], path[i + 1], amountOut);
        }
        return amountOut;
    }

    function _getAmountsInWithCurve(GetAmountsParams memory _params) internal view returns (uint256[3] memory) {
        (address[] memory path, address[] memory pools) = abi.decode(_params.encodedPath, (address[], address[]));
        uint256 inverseAmount = _getExchangeAmountCurve(
            _params.amount,
            _params.dexRouter,
            _reverseArray(path),
            _reverseArray(pools)
        );
        //expanding the search to +50% and -50% from the inverseAmount
        // mul by 0.5 WAD
        uint256 minValue = inverseAmount.wmul(5e17);
        // mul by 1.5 WAD
        uint256 maxValue = inverseAmount.wmul(15e17);
        uint256 amountIn;
        for (uint256 i; i <= 100; i++) {
            // first iteration checks the inverseAmount: (0.5 + 1.5) / 2
            uint256 middle = (minValue + maxValue) / 2;
            if (middle == amountIn) break;
            amountIn = middle;
            uint256 amountOut = _getExchangeAmountCurve(amountIn, _params.dexRouter, path, pools);
            if (amountOut == _params.amount) break;
            else if (amountOut < _params.amount) minValue = amountIn;
            else maxValue = amountIn;
        }
        return [amountIn, _params.amount, 0];
    }

    function _getAmountsWithUniswapV2(GetAmountsParams memory _params) internal view returns (uint256[3] memory) {
        address[] memory path = abi.decode(_params.encodedPath, (address[]));

        uint256[] memory amounts = IUniswapV2Router02(_params.dexRouter).getAmountsOut(_params.amount, path);

        return [_params.amount, amounts[amounts.length - 1], 0];
    }

    function _getAmountsInWithUniswapV2(GetAmountsParams memory _params) internal view returns (uint256[3] memory) {
        address[] memory path = abi.decode(_params.encodedPath, (address[]));

        uint256[] memory amounts = IUniswapV2Router02(_params.dexRouter).getAmountsIn(_params.amount, path);

        return [amounts[0], _params.amount, 0];
    }

    function _reverseArray(address[] memory source) internal pure returns (address[] memory) {
        uint256 length = source.length;
        address[] memory result = new address[](length);
        for (uint256 i; i < length; i++) {
            result[length - 1 - i] = source[i];
        }
        return result;
    }

    function _checkAmountsParams(GetAmountsParams memory _params) internal pure {
        _require(_params.dexRouter != address(0), Errors.ADDRESS_NOT_SUPPORTED.selector);
        _require(_params.amount != 0, Errors.ZERO_AMOUNT.selector);
    }

    function _getBalancerSwapSteps(
        address[] memory path,
        bytes32[] memory pools,
        uint256 amount
    ) internal pure returns (IAsset[] memory assets, IBalancer.BatchSwapStep[] memory steps) {
        assets = new IAsset[](path.length);
        steps = new IBalancer.BatchSwapStep[](pools.length);

        for (uint256 i; i < path.length - 1; i++) {
            assets[i] = IAsset(path[i]);
            steps[i] = IBalancer.BatchSwapStep({
                poolId: pools[i],
                assetInIndex: i,
                assetOutIndex: i + 1,
                amount: 0,
                userData: "0x"
            });
        }
        steps[0].amount = amount;
        assets[path.length - 1] = IAsset(path[path.length - 1]);
    }

    function _swapWithUniswapV2(SwapParams memory _params) private returns (uint256[3] memory) {
        if (_params.tokenIn == NATIVE_CURRENCY) {
            WNative.deposit{value: _params.amountIn}();
        }

        address[] memory path = abi.decode(_params.encodedPath, (address[]));
        TokenApproveLibrary.doApprove(path[0], _params.dexRouter, _params.amountIn);
        uint256[] memory amounts;

        amounts = IUniswapV2Router02(_params.dexRouter).swapExactTokensForTokens(
            _params.amountIn,
            _params.amountOutMin,
            path,
            _params.to,
            _params.deadline
        );
        if (_params.tokenOut == NATIVE_CURRENCY) {
            WNative.withdraw(WNative.balanceOf(address(this)));
        }
        return [_params.amountIn, amounts[amounts.length - 1], 0];
    }

    function _swapWithUniswapV3(SwapParams memory _params) private returns (uint256[3] memory) {
        if (_params.tokenIn == NATIVE_CURRENCY) {
            WNative.deposit{value: _params.amountIn}();
        }

        address tokenIn = _params.encodedPath.decodeFirstToken();
        TokenApproveLibrary.doApprove(tokenIn, _params.dexRouter, _params.amountIn);

        ISwapRouter.ExactInputParams memory params = ISwapRouter.ExactInputParams({
            path: _params.encodedPath,
            recipient: _params.to,
            deadline: _params.deadline,
            amountIn: _params.amountIn,
            amountOutMinimum: _params.amountOutMin
        });
        uint256 amountOut;
        // slither-disable-next-line unused-return
        try ISwapRouter(_params.dexRouter).exactInput(params) returns (uint256 _amountOut) {
            // slither-disable-next-line variable-scope
            amountOut = _amountOut;
            if (_params.tokenOut == NATIVE_CURRENCY) {
                WNative.withdraw(WNative.balanceOf(address(this)));
            }
        } catch Error(string memory err) {
            // slither-disable-next-line variable-scope
            revert(err);
        } catch (bytes memory) {
            _revert(Errors.REVERTED_WITHOUT_A_STRING_TRY_TO_CHECK_THE_ANCILLARY_DATA.selector);
        }

        return [_params.amountIn, amountOut, 0];
    }

    function _swapWithAlgebraV3(SwapParams memory _params) private returns (uint256[3] memory) {
        if (_params.tokenIn == NATIVE_CURRENCY) {
            WNative.deposit{value: _params.amountIn}();
        }

        address tokenIn = _params.encodedPath.decodeFirstToken();
        TokenApproveLibrary.doApprove(tokenIn, _params.dexRouter, _params.amountIn);

        ISwapRouterAlgebraV3.ExactInputParams memory params = ISwapRouterAlgebraV3.ExactInputParams({
            path: _params.encodedPath,
            recipient: _params.to,
            deadline: _params.deadline,
            amountIn: _params.amountIn,
            amountOutMinimum: _params.amountOutMin
        });
        uint256 amountOut = ISwapRouterAlgebraV3(_params.dexRouter).exactInput(params);
        if (_params.tokenOut == NATIVE_CURRENCY) {
            WNative.withdraw(WNative.balanceOf(address(this)));
        }
        return [_params.amountIn, amountOut, 0];
    }

    function _swapWithParaswap(SwapParams memory _params) private returns (uint256[3] memory) {
        uint256 balance = IERC20(_params.tokenOut).balanceOf(_params.to);

        if (_params.tokenIn != NATIVE_CURRENCY) {
            TokenApproveLibrary.doApprove(
                _params.tokenIn,
                IAugustusSwapper(_params.dexRouter).getTokenTransferProxy(),
                _params.amountIn
            );
        }
        // we just pass all payload data to the target router
        Address.functionCallWithValue(
            _params.dexRouter,
            _params.encodedPath,
            _params.tokenIn == NATIVE_CURRENCY ? _params.amountIn : 0
        );

        balance = IERC20(_params.tokenOut).balanceOf(_params.to) - balance;
        _require(balance >= _params.amountOutMin, Errors.SLIPPAGE_TOLERANCE_EXCEEDED.selector);

        return [_params.amountIn, balance, 0];
    }

    function _swapWithEnso(SwapParams memory _params) private returns (uint256[3] memory) {
        uint256 balance = IERC20(_params.tokenOut).balanceOf(_params.to);

        if (_params.tokenIn != NATIVE_CURRENCY) {
            TokenApproveLibrary.doApprove(_params.tokenIn, _params.dexRouter, _params.amountIn);
        }
        // we just pass all payload data to the target router
        Address.functionCallWithValue(
            _params.dexRouter,
            _params.encodedPath,
            _params.tokenIn == NATIVE_CURRENCY ? _params.amountIn : 0
        );

        balance = IERC20(_params.tokenOut).balanceOf(_params.to) - balance;
        _require(balance >= _params.amountOutMin, Errors.SLIPPAGE_TOLERANCE_EXCEEDED.selector);

        return [_params.amountIn, balance, 0];
    }

    function _swapWithArbitraryDex(SwapParams memory _params) private returns (uint256[3] memory) {
        uint256 balance = IERC20(_params.tokenOut).balanceOf(_params.to);

        (address spender, address dexRouter, bytes memory encodedPath) = abi.decode(
            _params.encodedPath,
            (address, address, bytes)
        );

        if (_params.tokenIn != NATIVE_CURRENCY) {
            TokenApproveLibrary.doApprove(_params.tokenIn, spender, _params.amountIn);
        }
        // we just pass all payload data to the target router
        Address.functionCallWithValue(
            dexRouter,
            encodedPath,
            _params.tokenIn == NATIVE_CURRENCY ? _params.amountIn : 0
        );

        balance = IERC20(_params.tokenOut).balanceOf(_params.to) - balance;
        _require(balance >= _params.amountOutMin, Errors.SLIPPAGE_TOLERANCE_EXCEEDED.selector);

        return [_params.amountIn, balance, 0];
    }

    function _swapWithCurve(SwapParams memory _params) private returns (uint256[3] memory) {
        // Curve does not check the deadline hence this check is necessary before the swap.
        _require(_params.deadline >= block.timestamp, Errors.SWAP_DEADLINE_PASSED.selector);
        (address[] memory path, address[] memory pools) = abi.decode(_params.encodedPath, (address[], address[]));
        uint256 amountOut = _params.amountIn;

        // we don't wrap and unwrap eth since the curve can handle the native currency
        for (uint256 i; i < path.length - 1; i++) {
            //'cause in the curve the native currency is denoted as 0xEeE...
            if (path[i] == NATIVE_CURRENCY_CURVE) {
                amountOut = ICurveRouter(_params.dexRouter).exchange{value: amountOut}(
                    pools[i],
                    path[i],
                    path[i + 1],
                    amountOut,
                    i == path.length - 2 ? _params.amountOutMin : 0,
                    i == path.length - 2 ? _params.to : address(this)
                );
            } else {
                TokenApproveLibrary.doApprove(path[i], _params.dexRouter, amountOut);
                amountOut = ICurveRouter(_params.dexRouter).exchange(
                    pools[i],
                    path[i],
                    path[i + 1],
                    amountOut,
                    i == path.length - 2 ? _params.amountOutMin : 0,
                    i == path.length - 2 ? _params.to : address(this)
                );
            }
        }

        return [_params.amountIn, amountOut, 0];
    }

    function _swapWithBalancer(SwapParams memory _params) private returns (uint256[3] memory) {
        (address[] memory path, bytes32[] memory pools, int256[] memory limits) = abi.decode(
            _params.encodedPath,
            (address[], bytes32[], int256[])
        );
        _require(path.length >= 2, Errors.INCORRECT_PATH.selector);

        if (_params.tokenIn == NATIVE_CURRENCY) {
            WNative.deposit{value: _params.amountIn}();
        }

        TokenApproveLibrary.doApprove(path[0], _params.dexRouter, _params.amountIn);

        IBalancer.FundManagement memory fundManagement = IBalancer.FundManagement({
            sender: address(this),
            fromInternalBalance: false,
            recipient: payable(_params.to),
            toInternalBalance: false
        });

        if (path.length > 2) {
            (IAsset[] memory assets, IBalancer.BatchSwapStep[] memory steps) = _getBalancerSwapSteps(
                path,
                pools,
                _params.amountIn
            );

            int256[] memory deltas;
            deltas = IBalancer(_params.dexRouter).batchSwap(
                IBalancer.SwapKind.GIVEN_IN,
                steps,
                assets,
                fundManagement,
                limits,
                _params.deadline
            );
            if (_params.tokenOut == NATIVE_CURRENCY) {
                WNative.withdraw(WNative.balanceOf(address(this)));
            }
            return [_params.amountIn, uint256(deltas[deltas.length - 1] * -1), 0];
        }

        IBalancer.SingleSwap memory singleSwap = IBalancer.SingleSwap({
            poolId: pools[0],
            kind: IBalancer.SwapKind.GIVEN_IN,
            assetIn: IAsset(path[0]),
            assetOut: IAsset(path[1]),
            amount: _params.amountIn,
            userData: "0x"
        });
        uint256 amountOut = IBalancer(_params.dexRouter).swap(
            singleSwap,
            fundManagement,
            _params.amountOutMin,
            _params.deadline
        );
        if (_params.tokenOut == NATIVE_CURRENCY) {
            WNative.withdraw(WNative.balanceOf(address(this)));
        }
        return [_params.amountIn, amountOut, 0];
    }
}

contracts/EPMXPriceFeed.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {AggregatorV2V3Interface} from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV2V3Interface.sol";

import "./libraries/Errors.sol";

import {SMALL_TIMELOCK_ADMIN} from "./Constants.sol";
import {IEPMXPriceFeed} from "./interfaces/IEPMXPriceFeed.sol";

contract EPMXPriceFeed is IEPMXPriceFeed, AggregatorV2V3Interface {
    uint80 internal constant MAX_UINT80_HEX = type(uint80).max;

    struct RoundData {
        uint80 roundId;
        int256 answer;
        uint256 startedAt;
        uint256 updatedAt;
        uint80 answeredInRound;
    }

    address public immutable registry;
    uint8 public override decimals = 8; // USD decimals
    uint80 private _currentRound;
    RoundData private _latestRoundData;
    mapping(uint80 => RoundData) private _answers;

    /**
     * @dev Modifier that checks if the caller has a specific role.
     * @param _role The role identifier to check.
     */
    modifier onlyRole(bytes32 _role) {
        _require(IAccessControl(registry).hasRole(_role, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    constructor(address _registry) {
        _require(
            IERC165(_registry).supportsInterface(type(IAccessControl).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        registry = _registry;
    }

    /**
     * @notice Retrieves the latest answer from the oracle.
     * @return The latest answer, with a precision of 8 decimal (USD decimals).
     */
    function latestAnswer() external view override returns (int256) {
        return _latestRoundData.answer;
    }

    /**
     * @notice Returns the latest round data.
     * @return roundId The ID of the latest round.
     * @return answer The answer provided in the latest round.
     * @return startedAt The timestamp when the latest round started.
     * @return updatedAt The timestamp when the latest round was updated.
     * @return answeredInRound The round in which the latest answer was provided.
     */
    function latestRoundData() external view override returns (uint80, int256, uint256, uint256, uint80) {
        return (
            _latestRoundData.roundId,
            _latestRoundData.answer,
            _latestRoundData.startedAt,
            _latestRoundData.updatedAt,
            _latestRoundData.answeredInRound
        );
    }

    /**
     * @notice Retrieves the latest timestamp of the round data.
     * @return The latest timestamp of when the round was updated, in seconds (UTC).
     */
    function latestTimestamp() external view override returns (uint256) {
        return _latestRoundData.updatedAt;
    }

    /**
     * @notice Retrieves the answer for a given round ID.
     * @param roundId The round ID for which to get the answer.
     * @return The answer previously set with setAnswer().
     */
    function getAnswer(uint256 roundId) external view override returns (int256) {
        if (roundId > MAX_UINT80_HEX) {
            return 0;
        }
        return _answers[uint80(roundId)].answer;
    }

    /**
     * @notice Retrieves the timestamp of a given round ID.
     * @param roundId The round ID for which to retrieve the timestamp.
     * @return The timestamp when the round was last updated.
     */
    function getTimestamp(uint256 roundId) external view override returns (uint256) {
        if (roundId > MAX_UINT80_HEX) {
            return 0;
        }
        return _answers[uint80(roundId)].updatedAt;
    }

    /**
     * @notice Retrieves the latest round ID.
     * @return The latest round ID.
     */
    function latestRound() external view override returns (uint256) {
        return _latestRoundData.roundId;
    }

    /**
     * @inheritdoc IEPMXPriceFeed
     */
    function setAnswer(int256 answer) public override onlyRole(SMALL_TIMELOCK_ADMIN) {
        _latestRoundData.answer = answer;
        _latestRoundData.roundId = _currentRound;
        _latestRoundData.startedAt = block.timestamp;
        _latestRoundData.updatedAt = block.timestamp;
        _latestRoundData.answeredInRound = _currentRound;
        _answers[_currentRound] = _latestRoundData;
        _currentRound++;
        emit AnswerUpdated(answer, _latestRoundData.roundId, block.timestamp);
    }

    /**
     * @notice Retrieves the data for a specific round.
     * @param _roundId The ID of the round to retrieve data for.
     * @return roundId The ID of the round.
     * @return answer The answer for the round.
     * @return startedAt The timestamp when the round started.
     * @return updatedAt The timestamp when the round was last updated.
     * @return answeredInRound The ID of the round in which the answer was computed.
     */
    function getRoundData(uint80 _roundId) public view override returns (uint80, int256, uint256, uint256, uint80) {
        RoundData memory roundData = _answers[_roundId];
        return (
            roundData.roundId,
            roundData.answer,
            roundData.startedAt,
            roundData.updatedAt,
            roundData.answeredInRound
        );
    }

    /**
     * @notice Returns the description of the contract.
     * @return The description string "EPMX / USD".
     */
    function description() public pure override returns (string memory) {
        return "EPMX / USD";
    }

    /**
     * @notice This function provides the version number of the contract.
     */
    function version() public pure override returns (uint256) {
        return 0;
    }
}

contracts/EPMXToken.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";

import "./libraries/Errors.sol";

import {BIG_TIMELOCK_ADMIN} from "./Constants.sol";
import {IEPMXToken} from "./interfaces/IEPMXToken.sol";

contract EPMXToken is IEPMXToken, ERC20, ERC165 {
    address public immutable registry;
    mapping(address => bool) public whitelist;

    /**
     * @dev Modifier that checks if the caller has a specific role.
     * @param _role The role identifier to check.
     */
    modifier onlyRole(bytes32 _role) {
        _require(IAccessControl(registry).hasRole(_role, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    constructor(address _recipient, address _registry) ERC20("Early Primex Token", "ePMX") {
        _require(
            ERC165(_registry).supportsInterface(type(IAccessControl).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        registry = _registry;

        if (_recipient == address(0)) {
            _recipient = msg.sender;
        }
        _mint(_recipient, 1000000000 * 10 ** decimals());
    }

    /**
     * @inheritdoc IEPMXToken
     */
    function burn(uint256 _amount) external override {
        _burn(msg.sender, _amount);
        emit Burn(msg.sender, _amount);
    }

    /**
     * @inheritdoc IEPMXToken
     */
    function addAddressToWhitelist(address _address) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        _require(!whitelist[_address], Errors.ADDRESS_ALREADY_WHITELISTED.selector);
        whitelist[_address] = true;
        emit WhitelistedAddressAdded(_address);
    }

    /**
     * @inheritdoc IEPMXToken
     */
    function addAddressesToWhitelist(address[] memory _addresses) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        for (uint256 i; i < _addresses.length; i++) {
            addAddressToWhitelist(_addresses[i]);
        }
    }

    /**
     * @inheritdoc IEPMXToken
     */
    function removeAddressFromWhitelist(address _address) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        _require(whitelist[_address], Errors.ADDRESS_NOT_WHITELISTED.selector);
        whitelist[_address] = false;
        emit WhitelistedAddressRemoved(_address);
    }

    /**
     * @inheritdoc IEPMXToken
     */
    function removeAddressesFromWhitelist(address[] calldata _addresses) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        for (uint256 i; i < _addresses.length; i++) {
            removeAddressFromWhitelist(_addresses[i]);
        }
    }

    /**
     * @inheritdoc IEPMXToken
     */
    function isWhitelisted(address _address) public view override returns (bool) {
        return whitelist[_address];
    }

    /**
     * @notice Interface checker
     * @param interfaceId The interface id to check
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IEPMXToken).interfaceId ||
            interfaceId == type(IERC20).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * @dev Hook that is called before any token transfer.
     * It enforces the restriction that the `from` or `to` address must be on the whitelist,
     * or the `from` address must be the zero address for minting.
     * @param from The address transferring the tokens. Use the zero address for minting.
     * @param to The address receiving the tokens.
     */
    function _beforeTokenTransfer(address from, address to, uint256 /* amount */) internal virtual override(ERC20) {
        _require(
            whitelist[from] || whitelist[to] || from == address(0),
            Errors.RECIPIENT_OR_SENDER_MUST_BE_ON_WHITE_LIST.selector
        );
    }
}

contracts/FlashLoanManager/FlashLoanMana.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {WadRayMath} from "../libraries/utils/WadRayMath.sol";

import {TokenTransfersLibrary} from "../libraries/TokenTransfersLibrary.sol";
import {FlashLoanManagerStorage, IERC165Upgradeable} from "./FlashLoanManagerStorage.sol";
import {BIG_TIMELOCK_ADMIN, SMALL_TIMELOCK_ADMIN, EMERGENCY_ADMIN, FLASH_LOAN_FREE_BORROWER_ROLE} from "../Constants.sol";
import "../libraries/Errors.sol";

import {IFlashLoanReceiver} from "../interfaces/IFlashLoanReceiver.sol";
import {IFlashLoanManager, IPausable} from "./IFlashLoanManager.sol";
import {IBucketV4} from "../Bucket/IBucket.sol";
import {IPrimexDNS} from "../PrimexDNS/IPrimexDNS.sol";
import {IPrimexDNSStorage} from "../PrimexDNS/IPrimexDNSStorage.sol";
import {IWhiteBlackList} from "../WhiteBlackList/WhiteBlackList/IWhiteBlackList.sol";

contract FlashLoanManager is IFlashLoanManager, FlashLoanManagerStorage {
    using WadRayMath for uint256;

    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Throws if caller is not granted with _role
     * @param _role The role that is being checked for a function caller
     */
    modifier onlyRole(bytes32 _role) {
        _require(registry.hasRole(_role, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    /**
     * @dev Modifier to check if the sender is not blacklisted.
     */
    modifier notBlackListed() {
        _require(!whiteBlackList.isBlackListed(msg.sender), Errors.SENDER_IS_BLACKLISTED.selector);
        _;
    }

    /**
     * @inheritdoc IFlashLoanManager
     */
    function initialize(
        address _registry,
        address _primexDNS,
        address _whiteBlackList,
        uint256 _flashLoanFeeRate,
        uint256 _flashLoanProtocolRate
    ) external override initializer {
        _require(
            IERC165Upgradeable(_registry).supportsInterface(type(IAccessControl).interfaceId) &&
                IERC165Upgradeable(_primexDNS).supportsInterface(type(IPrimexDNS).interfaceId) &&
                IERC165Upgradeable(_whiteBlackList).supportsInterface(type(IWhiteBlackList).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        whiteBlackList = IWhiteBlackList(_whiteBlackList);
        registry = IAccessControl(_registry);
        primexDNS = IPrimexDNS(_primexDNS);
        _setFlashLoanRates(_flashLoanFeeRate, _flashLoanProtocolRate);
    }

    /**
     * @inheritdoc IFlashLoanManager
     */
    function setFlashLoanRates(
        uint256 _newFlashLoanFeeRate,
        uint256 _newFlashLoanProtocolRate
    ) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _setFlashLoanRates(_newFlashLoanFeeRate, _newFlashLoanProtocolRate);
    }

    /**
     * @inheritdoc IFlashLoanManager
     */
    function flashLoan(
        address receiver,
        address[] calldata buckets,
        uint256[] calldata amounts,
        bytes calldata params
    ) external override nonReentrant notBlackListed whenNotPaused {
        _require(buckets.length == amounts.length, Errors.INCONSISTENT_FLASHLOAN_PARAMS.selector);
        _hasNoBucketsDuplicates(buckets);
        bool isZeroFee = registry.hasRole(FLASH_LOAN_FREE_BORROWER_ROLE, msg.sender);
        address[] memory assets = new address[](buckets.length);
        uint256[] memory flashLoanFees = new uint256[](buckets.length);
        uint256[] memory availableLiquidities = new uint256[](buckets.length);

        for (uint256 i; i < buckets.length; i++) {
            IBucketV4 bucket = IBucketV4(buckets[i]);
            (address bucketAddress, IPrimexDNSStorage.Status status, , ) = primexDNS.buckets(bucket.name());
            _require(buckets[i] == bucketAddress, Errors.BUCKET_OUTSIDE_PRIMEX_PROTOCOL.selector);
            _require(status == IPrimexDNSStorage.Status.Active, Errors.BUCKET_IS_NOT_ACTIVE.selector);
            assets[i] = address(bucket.borrowedAsset());
            flashLoanFees[i] = isZeroFee ? 0 : amounts[i].wmul(flashLoanFeeRate);
            availableLiquidities[i] = bucket.availableLiquidity();
            bucket.performFlashLoanTransfer(receiver, amounts[i]);
        }

        _require(
            IFlashLoanReceiver(receiver).executeOperation(assets, amounts, flashLoanFees, msg.sender, params),
            Errors.INVALID_FLASHLOAN_EXECUTOR_RETURN.selector
        );

        for (uint256 i; i < buckets.length; i++) {
            _handleFlashLoanRepayment(
                IBucketV4(buckets[i]),
                assets[i],
                amounts[i],
                flashLoanFees[i],
                availableLiquidities[i],
                receiver
            );
        }
    }

    /**
     * @inheritdoc IPausable
     */
    function pause() external override onlyRole(EMERGENCY_ADMIN) {
        _pause();
    }

    /**
     * @inheritdoc IPausable
     */
    function unpause() external override onlyRole(SMALL_TIMELOCK_ADMIN) {
        _unpause();
    }

    /**
     * @notice Interface checker
     * @param _interfaceId The interface id to check
     */
    function supportsInterface(bytes4 _interfaceId) public view virtual override returns (bool) {
        return _interfaceId == type(IFlashLoanManager).interfaceId || super.supportsInterface(_interfaceId);
    }

    /**
     * @notice Handles repayment of flashloaned assets + flashLoanFee
     * @dev Will pull the amount + flashLoanFee from the receiver to the Bucket and Treasury
     */
    function _handleFlashLoanRepayment(
        IBucketV4 bucket,
        address asset,
        uint256 amount,
        uint256 flashLoanFee,
        uint256 availableLiquidity,
        address receiver
    ) internal {
        uint256 feeToTreasury = flashLoanFee.wmul(flashLoanProtocolRate);
        uint256 feeToBucket = flashLoanFee - feeToTreasury;
        uint256 amountPlusFeeToBucket = amount + feeToBucket;
        // Accumulates a predefined amount of asset to the bucket as a fixed, instantaneous income
        bucket.cumulateToLiquidityIndex(feeToBucket, availableLiquidity);

        // The receiver should approve the flash loaned amount + fee for the FlashLoanManager.
        TokenTransfersLibrary.doTransferFromTo(asset, receiver, address(bucket), amountPlusFeeToBucket);
        TokenTransfersLibrary.doTransferFromTo(asset, receiver, primexDNS.treasury(), feeToTreasury);

        bucket.updateRates();
        emit FlashLoan(receiver, msg.sender, asset, amount, flashLoanFee, feeToTreasury);
    }

    function _setFlashLoanRates(uint256 _newFlashLoanFeeRate, uint256 _newFlashLoanProtocolRate) internal {
        _require(_newFlashLoanFeeRate <= WadRayMath.WAD / 10, Errors.FLASH_LOAN_FEE_RATE_IS_MORE_10_PERCENT.selector);
        _require(
            _newFlashLoanProtocolRate <= WadRayMath.WAD / 2,
            Errors.FLASH_LOAN_PROTOCOL_RATE_IS_MORE_50_PERCENT.selector
        );
        flashLoanFeeRate = _newFlashLoanFeeRate;
        flashLoanProtocolRate = _newFlashLoanProtocolRate;
        emit ChangedFlashLoanRates(_newFlashLoanFeeRate, _newFlashLoanProtocolRate);
    }

    /**
     * @notice Checks if an array of has no duplicate addresses.
     * @param _addresses The array of buckets addresses to be checked. The array should be sorted in ascending order before being passed.
     */
    function _hasNoBucketsDuplicates(address[] calldata _addresses) internal pure {
        if (_addresses.length > 1) {
            for (uint256 i = 1; i < _addresses.length; i++) {
                _require(_addresses[i - 1] < _addresses[i], Errors.SHOULD_NOT_HAVE_DUPLICATES.selector);
            }
        }
    }
}

