pragma solidity 0.8.26;

import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {ERC165Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

import "../libraries/Errors.sol";

import {IWhiteBlackList} from "../WhiteBlackList/WhiteBlackList/IWhiteBlackList.sol";
import {IKeeperRewardDistributorStorage, IKeeperRewardDistributorStorageV2} from "./IKeeperRewardDistributorStorage.sol";

abstract contract KeeperRewardDistributorStorage is
    IKeeperRewardDistributorStorage,
    ReentrancyGuardUpgradeable,
    PausableUpgradeable,
    ERC165Upgradeable
{
    address public override priceOracle;
    address public override registry;
    address public override pmx;
    address payable public override treasury;
    uint256 public override pmxPartInReward;
    uint256 public override nativePartInReward;
    /// @custom:oz-renamed-from positionSizeCoefficientA
    uint256 public override positionSizeCoefficient;
    int256 public override positionSizeCoefficientB;
    uint256 public override additionalGas;
    uint256 public override defaultMaxGasPrice;
    uint256 public override oracleGasPriceTolerance;
    PaymentModel public override paymentModel;
    mapping(address => KeeperBalance) public override keeperBalance;
    KeeperBalance public override totalBalance;
    mapping(KeeperActionType => KeeperActionRewardConfig) public override maxGasPerPosition;
    mapping(KeeperCallingMethod => DataLengthRestrictions) public override dataLengthRestrictions;
    mapping(DecreasingReason => uint256) public override decreasingGasByReason;
    IWhiteBlackList internal whiteBlackList;
}

abstract contract KeeperRewardDistributorStorageV2 is
    IKeeperRewardDistributorStorageV2,
    KeeperRewardDistributorStorage
{
    /// @custom:oz-retyped-from int256
    /// @custom:oz-renamed-from minPositionSizeMultiplier
    uint256 public override minPositionSizeAddend;
    uint256 public override optimisticGasCoefficient;
}

contracts/lens/BestDexLens.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {WadRayMath} from "../libraries/utils/WadRayMath.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";

import {LimitOrderLibrary} from "../libraries/LimitOrderLibrary.sol";
import {PrimexPricingLibrary} from "../libraries/PrimexPricingLibrary.sol";
import {PositionLibrary} from "../libraries/PositionLibrary.sol";
import "./../libraries/Errors.sol";

import {IBestDexLens} from "../interfaces/IBestDexLens.sol";
import {IPositionManagerV2} from "../PositionManager/IPositionManager.sol";
import {IPrimexDNSV3} from "../PrimexDNS/IPrimexDNS.sol";
import {ILimitOrderManager} from "../LimitOrderManager/ILimitOrderManager.sol";
import {IDexAdapter} from "../interfaces/IDexAdapter.sol";

/**
 * @dev  All functions in this contract are intended to be called off-chain. Do not call functions from other contracts to avoid an out-of-gas error.
 */
contract BestDexLens is IBestDexLens, IERC165 {
    using WadRayMath for uint256;
    using SafeCast for uint256;

    int256 internal constant VERY_NEGATIVE_VALUE = -1e72;
    int256 internal constant VERY_POSITIVE_VALUE = 1e72;

    /**
     * @inheritdoc IBestDexLens
     */
    function getBestDexByOrder(
        BestDexByOrderParams memory _params
    ) external payable override returns (GetBestDexByOrderReturnParams memory _returnParams) {
        _require(
            IERC165(address(_params.positionManager)).supportsInterface(type(IPositionManagerV2).interfaceId) &&
                IERC165(address(_params.limitOrderManager)).supportsInterface(type(ILimitOrderManager).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        _params.positionManager.priceOracle().updatePullOracle{value: msg.value}(
            _params.pullOracleData,
            _params.pullOracleTypes
        );

        LimitOrderLibrary.LimitOrder memory order = _params.limitOrderManager.getOrder(_params.orderId);
        address borrowedAsset = order.leverage == WadRayMath.WAD
            ? order.depositAsset
            : address(order.bucket.borrowedAsset());
        IPrimexDNSV3 primexDns = _params.positionManager.primexDNS();

        bool isBorrowedAsset = borrowedAsset == order.depositAsset;
        bool isThirdAsset = !isBorrowedAsset && order.depositAsset != order.positionAsset;

        if (!isBorrowedAsset) {
            _returnParams.depositToBorrowedReturnParams = getBestMultipleDexes(
                GetBestMultipleDexesParams({
                    positionManager: _params.positionManager,
                    assetToBuy: borrowedAsset,
                    assetToSell: order.depositAsset,
                    amount: order.depositAmount,
                    isAmountToBuy: false,
                    shares: _params.shares.depositToBorrowedShares,
                    gasPriceInCheckedAsset: 0,
                    dexes: _params.dexes
                })
            );
        }

        uint256 depositAmountInBorrowed = PrimexPricingLibrary.getDepositAmountInBorrowed(
            IDexAdapter.AmountParams({
                tokenA: order.depositAsset,
                tokenB: borrowedAsset,
                amount: order.depositAmount,
                megaRoutes: _returnParams.depositToBorrowedReturnParams.megaRoutes
            }),
            isThirdAsset,
            payable(primexDns.dexAdapter()),
            address(_params.positionManager.priceOracle()),
            _params.depositBorrowedAssetOracleData
        );

        uint256 amountToTransfer = depositAmountInBorrowed.wmul(order.leverage - WadRayMath.WAD);

        if (isBorrowedAsset) {
            amountToTransfer += depositAmountInBorrowed;
        } else if (isThirdAsset) {
            _returnParams.depositInThirdAssetReturnParams = getBestMultipleDexes(
                GetBestMultipleDexesParams({
                    positionManager: _params.positionManager,
                    assetToBuy: order.positionAsset,
                    assetToSell: order.depositAsset,
                    amount: order.depositAmount,
                    isAmountToBuy: false,
                    shares: _params.shares.depositInThirdAssetShares,
                    gasPriceInCheckedAsset: 0,
                    dexes: _params.dexes
                })
            );
        }

        _returnParams.firstAssetReturnParams = getBestMultipleDexes(
            GetBestMultipleDexesParams({
                positionManager: _params.positionManager,
                assetToBuy: order.positionAsset,
                assetToSell: borrowedAsset,
                amount: amountToTransfer,
                isAmountToBuy: false,
                shares: _params.shares.firstAssetShares,
                gasPriceInCheckedAsset: 0,
                dexes: _params.dexes
            })
        );
    }

    /**
     * @inheritdoc IBestDexLens
     */
    function getBestDexByPosition(
        IPositionManagerV2 _positionManager,
        uint256 _positionId,
        uint256 _shares,
        DexWithAncillaryData[] memory _dexesWithAncillaryData
    ) public override returns (GetBestMultipleDexesReturnParams memory) {
        _require(
            IERC165(address(_positionManager)).supportsInterface(type(IPositionManagerV2).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        PositionLibrary.Position memory position = _positionManager.getPosition(_positionId);

        return
            getBestMultipleDexes(
                GetBestMultipleDexesParams({
                    positionManager: _positionManager,
                    assetToBuy: position.soldAsset,
                    assetToSell: position.positionAsset,
                    amount: position.positionAmount,
                    isAmountToBuy: false,
                    shares: _shares,
                    gasPriceInCheckedAsset: 0,
                    dexes: _dexesWithAncillaryData
                })
            );
    }

    /**
     * @inheritdoc IBestDexLens
     */
    function getBestDexForOpenablePosition(
        BestDexForOpenablePositionParams memory _params
    )
        public
        override
        returns (
            GetBestMultipleDexesReturnParams memory _firstAssetReturnParams,
            GetBestMultipleDexesReturnParams memory _depositInThirdAssetReturnParams,
            GetBestMultipleDexesReturnParams memory _depositToBorrowedReturnParams
        )
    {
        _require(
            IERC165(address(_params.positionManager)).supportsInterface(type(IPositionManagerV2).interfaceId) &&
                _params.borrowedAsset != address(0) &&
                _params.depositAsset != address(0) &&
                _params.positionAsset != address(0),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        _require(_params.depositAmount != 0, Errors.DEPOSITED_AMOUNT_IS_0.selector);

        _require(
            _params.borrowedAmount != 0 || _params.borrowedAsset == _params.depositAsset,
            Errors.SPOT_DEPOSITED_ASSET_SHOULD_BE_EQUAL_BORROWED_ASSET.selector
        );

        bool isBorrowedAsset = _params.borrowedAsset == _params.depositAsset;
        bool isThirdAsset = !isBorrowedAsset && _params.depositAsset != _params.positionAsset;
        if (!isBorrowedAsset) {
            _depositToBorrowedReturnParams = getBestMultipleDexes(
                GetBestMultipleDexesParams({
                    positionManager: _params.positionManager,
                    assetToBuy: _params.borrowedAsset,
                    assetToSell: _params.depositAsset,
                    amount: _params.depositAmount,
                    isAmountToBuy: false,
                    shares: _params.shares.depositToBorrowedShares,
                    gasPriceInCheckedAsset: 0,
                    dexes: _params.dexes
                })
            );
        }
        if (isThirdAsset) {
            _depositInThirdAssetReturnParams = getBestMultipleDexes(
                GetBestMultipleDexesParams({
                    positionManager: _params.positionManager,
                    assetToBuy: _params.positionAsset,
                    assetToSell: _params.depositAsset,
                    amount: _params.depositAmount,
                    isAmountToBuy: false,
                    shares: _params.shares.depositInThirdAssetShares,
                    gasPriceInCheckedAsset: 0,
                    dexes: _params.dexes
                })
            );
        }
        _firstAssetReturnParams = getBestMultipleDexes(
            GetBestMultipleDexesParams({
                positionManager: _params.positionManager,
                assetToBuy: _params.positionAsset,
                assetToSell: _params.borrowedAsset,
                amount: _params.borrowedAmount + (isBorrowedAsset ? _params.depositAmount : 0),
                isAmountToBuy: false,
                shares: _params.shares.firstAssetShares,
                gasPriceInCheckedAsset: 0,
                dexes: _params.dexes
            })
        );
    }

    /**
     * @inheritdoc IBestDexLens
     */
    function getBestMultipleDexes(
        GetBestMultipleDexesParams memory _params
    ) public override returns (GetBestMultipleDexesReturnParams memory _returnParams) {
        _require(
            _params.assetToBuy != address(0) && _params.assetToSell != address(0),
            Errors.ZERO_ASSET_ADDRESS.selector
        );
        _require(_params.assetToBuy != _params.assetToSell, Errors.ASSETS_SHOULD_BE_DIFFERENT.selector);
        _require(_params.shares > 0, Errors.ZERO_SHARES.selector);
        _require(_params.amount >= _params.shares, Errors.SHARES_AMOUNT_IS_GREATER_THAN_AMOUNT_TO_SELL.selector);

        GetBestMultipleDexesVars memory vars;
        DexWithAncillaryData[] memory activeDexes = new DexWithAncillaryData[](_params.dexes.length);
        vars.shareCount = _params.shares;

        // stores estimated gas to perform swap on each DEX
        vars.gases = new uint256[](_params.dexes.length);

        // matrix [allDexes.length][shareCount] containing the swapped amount
        // on each DEX (rows) for each share (columns) minus estimated gas to perform the swap
        int256[][] memory amountByDexByShare = new int256[][](_params.dexes.length);

        vars.path = new address[](2);
        vars.path[0] = _params.assetToSell;
        vars.path[1] = _params.assetToBuy;

        // filter out inactive DEXes and collect their outputs for all possible share splits
        {
            IDexAdapter.GetAmountsParams memory amountParams;

            for (uint256 i; i < _params.dexes.length; i++) {
                // if DEX is not supported or deactivated - set expected gas to a very large number
                // slither-disable-next-line unused-return
                try _params.positionManager.primexDNS().getDexAddress(_params.dexes[i].dex) returns (
                    // slither-disable-next-line unused-return,variable-scope
                    address currentRouter
                ) {
                    uint256 returnGas = IDexAdapter(payable(_params.positionManager.primexDNS().dexAdapter())).getGas(
                        currentRouter
                    );
                    amountParams.amount = _params.amount / vars.shareCount;
                    amountParams.dexRouter = currentRouter;

                    amountParams.encodedPath = PrimexPricingLibrary.encodePath(
                        vars.path,
                        currentRouter,
                        _params.dexes[i].ancillaryData,
                        payable(_params.positionManager.primexDNS().dexAdapter()),
                        _params.isAmountToBuy
                    );

                    uint256 amount = _getAmountsFromAdapter(
                        amountParams,
                        IDexAdapter(payable(_params.positionManager.primexDNS().dexAdapter())),
                        _params.isAmountToBuy
                    );
                    if (amount == type(uint256).max) continue;
                    // add DEX to active list
                    activeDexes[vars.activeDexesLength] = _params.dexes[i];
                    amountByDexByShare[vars.activeDexesLength] = new int256[](vars.shareCount);
                    // store estimated gas
                    vars.gases[vars.activeDexesLength] = returnGas;
                    vars.gasInCheckedAsset = ((returnGas * _params.gasPriceInCheckedAsset) / 1e18).toInt256();
                    amountByDexByShare[vars.activeDexesLength][0] = _params.isAmountToBuy
                        ? amount.toInt256() + vars.gasInCheckedAsset
                        : amount.toInt256() - vars.gasInCheckedAsset;
                } catch {
                    continue;
                }
                for (uint256 j = 1; j < vars.shareCount; j++) {
                    amountParams.amount = (_params.amount * (j + 1)) / vars.shareCount;
                    uint256 amount = _getAmountsFromAdapter(
                        amountParams,
                        IDexAdapter(payable(_params.positionManager.primexDNS().dexAdapter())),
                        _params.isAmountToBuy
                    );

                    amountByDexByShare[vars.activeDexesLength][j] = _params.isAmountToBuy
                        ? (
                            amount == type(uint256).max
                                ? VERY_POSITIVE_VALUE
                                : amount.toInt256() + vars.gasInCheckedAsset
                        )
                        : (
                            amount == type(uint256).max
                                ? VERY_NEGATIVE_VALUE
                                : amount.toInt256() - vars.gasInCheckedAsset
                        );
                }

                // we should get here if first _getAmountsFromAdapter is successful and DEX is active
                vars.activeDexesLength++;
            }
        }

        _require(vars.activeDexesLength > 0, Errors.NO_ACTIVE_DEXES.selector);

        // array with best splitting route
        uint256[] memory distribution = new uint256[](vars.activeDexesLength);
        uint256 involvedDexesLength;

        {
            // matrix [activeDexesLength][shareCount] containing the maximum amount you receive for swapping
            // j parts of asset for each DEX
            int256[][] memory answer = new int256[][](vars.activeDexesLength);
            // matrix [activeDexesLength][shareCount] containing the amount of parts you should swap on previous DEXes
            // if you swap j parts on current DEX
            uint256[][] memory parentParts = new uint256[][](vars.activeDexesLength);

            for (uint256 i; i < vars.activeDexesLength; i++) {
                answer[i] = new int256[](vars.shareCount);
                parentParts[i] = new uint256[](vars.shareCount);
            }

            // copy first DEX from `amountByDexByShare` to the `answer` first row
            for (uint256 j; j < vars.shareCount; j++) {
                answer[0][j] = amountByDexByShare[0][j];
            }

            for (uint256 i = 1; i < vars.activeDexesLength; i++) {
                for (uint256 j; j < vars.shareCount; j++) {
                    // choose the value from the previous DEX as a max value
                    int256 bestValue = answer[i - 1][j];
                    // save current shares count
                    parentParts[i][j] = j + 1;
                    // current value is a sum of previous max shares so that total shares count is j + 1
                    int256 currentValue = amountByDexByShare[i][j];
                    if (
                        _params.isAmountToBuy ? (currentValue < bestValue || bestValue == 0) : currentValue > bestValue
                    ) {
                        bestValue = currentValue;
                        parentParts[i][j] = 0;
                    }

                    for (uint256 k; k < j; k++) {
                        currentValue = answer[i - 1][j - k - 1] + amountByDexByShare[i][k];

                        // if current value of DEX + previous value of previous DEX is higher than max value
                        // update max value and save previous shares count
                        if (
                            _params.isAmountToBuy
                                ? (currentValue < bestValue || bestValue == 0)
                                : currentValue > bestValue
                        ) {
                            bestValue = currentValue;
                            parentParts[i][j] = j - k;
                        }
                    }
                    answer[i][j] = bestValue;
                }
            }

            // iterate over `parentParts` backwards and collect the parts of the shares to get the resulting maximum amount
            {
                uint256 partsLeft = vars.shareCount;
                for (uint256 i; i < vars.activeDexesLength; i++) {
                    if (partsLeft == 0) break;
                    uint256 curExchange = vars.activeDexesLength - i - 1;
                    distribution[curExchange] = partsLeft - parentParts[curExchange][partsLeft - 1];
                    partsLeft = parentParts[curExchange][partsLeft - 1];
                    if (distribution[curExchange] > 0) {
                        involvedDexesLength++;
                    }
                }
            }
        }
        if (involvedDexesLength == 0) return _returnParams;
        _returnParams.megaRoutes = new PrimexPricingLibrary.MegaRoute[](1);

        _returnParams.megaRoutes[0] = PrimexPricingLibrary.MegaRoute({
            shares: 1, // 100%
            routes: new PrimexPricingLibrary.Route[](1)
        });

        _returnParams.megaRoutes[0].routes[0] = PrimexPricingLibrary.Route({
            to: _params.assetToBuy,
            paths: new PrimexPricingLibrary.Path[](involvedDexesLength)
        });

        for (uint256 i; i < vars.activeDexesLength; i++) {
            if (distribution[i] == 0) continue;

            _returnParams.megaRoutes[0].routes[0].paths[vars.filledRoutes] = PrimexPricingLibrary.Path({
                shares: distribution[i],
                dexName: activeDexes[i].dex,
                payload: PrimexPricingLibrary.encodePath(
                    vars.path,
                    _params.positionManager.primexDNS().getDexAddress(activeDexes[i].dex),
                    activeDexes[i].ancillaryData,
                    payable(_params.positionManager.primexDNS().dexAdapter()),
                    _params.isAmountToBuy
                )
            });
            vars.filledRoutes++;
            // collect some additional statistics: total return amount, estimate gas spending
            _returnParams.estimateGasAmount = _returnParams.estimateGasAmount.add(vars.gases[i]);
            int256 value = amountByDexByShare[i][distribution[i] - 1];
            _returnParams.returnAmount =
                _returnParams.returnAmount +
                uint256(
                    (
                        value == 0 ? int256(0) : _params.isAmountToBuy
                            ? (value - ((vars.gases[i] * _params.gasPriceInCheckedAsset) / 1e18).toInt256())
                            : (value + ((vars.gases[i] * _params.gasPriceInCheckedAsset) / 1e18).toInt256())
                    )
                );
        }
    }

    /**
     * @notice Interface checker
     * @param interfaceId The interface id to check
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(IBestDexLens).interfaceId || interfaceId == type(IERC165).interfaceId;
    }

    /**
     * @notice Retrieves the amounts from a DEX adapter contract.
     * @param _params The parameters for getting amounts from the adapter.
     * @param _adapter The DEX adapter contract.
     * @param _isAmountToBuy A flag indicating whether the amount to retrieve is for buying or selling.
     * @return The retrieved amount.
     */
    function _getAmountsFromAdapter(
        IDexAdapter.GetAmountsParams memory _params,
        IDexAdapter _adapter,
        bool _isAmountToBuy
    ) internal returns (uint256) {
        if (_isAmountToBuy) {
            try _adapter.getAmountsIn(_params) returns (uint256[3] memory answersList) {
                return answersList[0];
            } catch {
                return type(uint256).max;
            }
        }
        try _adapter.getAmountsOut(_params) returns (uint256[3] memory answersList) {
            return answersList[1];
        } catch {
            return type(uint256).max;
        }
    }
}

contracts/lens/PrimexLens.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import {WadRayMath} from "../libraries/utils/WadRayMath.sol";

import {PrimexPricingLibrary} from "../libraries/PrimexPricingLibrary.sol";
import {PositionLibrary} from "../libraries/PositionLibrary.sol";
import {LimitOrderLibrary} from "../libraries/LimitOrderLibrary.sol";
import "./../libraries/Errors.sol";

import {IPrimexLens} from "../interfaces/IPrimexLens.sol";
import {IBucketV3} from "../Bucket/IBucket.sol";
import {IBucketsFactory} from "../Bucket/IBucketsFactory.sol";
import {IPositionManagerV2} from "../PositionManager/IPositionManager.sol";
import {ILimitOrderManager} from "../LimitOrderManager/ILimitOrderManager.sol";
import {ITakeProfitStopLossCCM} from "../interfaces/ITakeProfitStopLossCCM.sol";
import {ILiquidityMiningRewardDistributor} from "../LiquidityMiningRewardDistributor/ILiquidityMiningRewardDistributor.sol";
import {IInterestRateStrategy} from "../interfaces/IInterestRateStrategy.sol";
import {IPrimexDNSV3, IPrimexDNSStorage, IPrimexDNSStorageV3} from "../PrimexDNS/PrimexDNS.sol";
import {IKeeperRewardDistributorStorage} from "../KeeperRewardDistributor/IKeeperRewardDistributorStorage.sol";
import {ARB_NITRO_ORACLE, GAS_FOR_BYTE, OVM_GASPRICEORACLE, TRANSACTION_METADATA_BYTES} from "../Constants.sol";

/**
 * @dev  All functions in this contract are intended to be called off-chain. Do not call functions from other contracts to avoid an out-of-gas error.
 */

contract PrimexLens is IPrimexLens, ERC165 {
    using WadRayMath for uint256;
    using PositionLibrary for PositionLibrary.Position;

    address public immutable takeProfitStopLossCCM;

    constructor(address _takeProfitStopLossCCM) {
        _require(
            IERC165(_takeProfitStopLossCCM).supportsInterface(type(ITakeProfitStopLossCCM).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        takeProfitStopLossCCM = _takeProfitStopLossCCM;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getOpenPositionsWithConditions(
        address _positionManager,
        uint256 _cursor,
        uint256 _count
    )
        external
        view
        override
        returns (OpenPositionWithConditions[] memory openPositionsWithConditions, uint256 newCursor)
    {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        uint256 positionsLength = IPositionManagerV2(_positionManager).getAllPositionsLength();
        if (_cursor >= positionsLength) {
            return (openPositionsWithConditions, 0);
        }
        if (_cursor + _count >= positionsLength) {
            _count = positionsLength - _cursor;
        } else {
            newCursor = _cursor + _count;
        }

        openPositionsWithConditions = new OpenPositionWithConditions[](_count);
        for (uint256 i; i < _count; i++) {
            openPositionsWithConditions[i].positionData = IPositionManagerV2(_positionManager).getPositionByIndex(
                _cursor + i
            );
            openPositionsWithConditions[i].conditionsData = IPositionManagerV2(_positionManager).getCloseConditions(
                openPositionsWithConditions[i].positionData.id
            );
        }
        return (openPositionsWithConditions, newCursor);
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getArrayOpenPositionDataByTrader(
        address _positionManager,
        address _trader,
        uint256 _cursor,
        uint256 _count
    ) external view override returns (OpenPositionData[] memory positionsData, uint256 newCursor) {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId) && _trader != address(0),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );

        uint256 positionsLength = IPositionManagerV2(_positionManager).getTraderPositionsLength(_trader);
        if (_cursor >= positionsLength) {
            return (positionsData, 0);
        }
        if (_cursor + _count >= positionsLength) {
            _count = positionsLength - _cursor;
        } else {
            newCursor = _cursor + _count;
        }

        positionsData = new OpenPositionData[](_count);
        for (uint256 i; i < _count; i++) {
            uint256 positionId = IPositionManagerV2(_positionManager).traderPositionIds(_trader, _cursor + i);
            positionsData[i] = getOpenPositionData(_positionManager, positionId);
        }
        return (positionsData, newCursor);
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getArrayOpenPositionDataByBucket(
        address _positionManager,
        address _bucket,
        uint256 _cursor,
        uint256 _count
    ) external view override returns (OpenPositionData[] memory positionsData, uint256 newCursor) {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId) && _bucket != address(0),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );

        uint256 positionsLength = IPositionManagerV2(_positionManager).getBucketPositionsLength(_bucket);
        if (_cursor >= positionsLength) {
            return (positionsData, 0);
        }
        if (_cursor + _count >= positionsLength) {
            _count = positionsLength - _cursor;
        } else {
            newCursor = _cursor + _count;
        }

        positionsData = new OpenPositionData[](_count);
        for (uint256 i; i < _count; i++) {
            uint256 positionId = IPositionManagerV2(_positionManager).bucketPositionIds(_bucket, _cursor + i);
            positionsData[i] = getOpenPositionData(_positionManager, positionId);
        }
        return (positionsData, newCursor);
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getAllBucketsFactory(
        address[] calldata _bucketFactories,
        address _user,
        address _positionManager,
        bool _showDeprecated
    ) external view override returns (BucketMetaData[] memory) {
        address[][] memory allBucketsArray = new address[][](_bucketFactories.length);
        for (uint256 i; i < _bucketFactories.length; i++) {
            allBucketsArray[i] = IBucketsFactory(_bucketFactories[i]).allBuckets();
        }
        uint256 totalBucketsCount;
        for (uint256 i; i < allBucketsArray.length; i++) {
            totalBucketsCount += allBucketsArray[i].length;
        }
        address[] memory buckets = new address[](totalBucketsCount);
        uint256 index;
        for (uint256 i; i < allBucketsArray.length; i++) {
            for (uint256 j; j < allBucketsArray[i].length; j++) {
                buckets[index] = allBucketsArray[i][j];
                index++;
            }
        }

        return getBucketsArray(buckets, _user, _positionManager, _showDeprecated);
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getChainlinkLatestRoundData(
        address[] calldata _feeds
    ) external view override returns (RoundData[] memory) {
        uint256 feedCount = _feeds.length;
        RoundData[] memory res = new RoundData[](feedCount);

        uint80 roundId;
        int256 answer;
        uint256 startedAt;
        uint256 updatedAt;
        uint80 answeredInRound;

        for (uint256 i; i < feedCount; i++) {
            _require(_feeds[i] != address(0), Errors.ADDRESS_NOT_SUPPORTED.selector);
            (roundId, answer, startedAt, updatedAt, answeredInRound) = AggregatorV3Interface(_feeds[i])
                .latestRoundData();
            res[i].roundId = roundId;
            res[i].answer = answer;
            res[i].startedAt = startedAt;
            res[i].updatedAt = updatedAt;
            res[i].answeredInRound = answeredInRound;
        }

        return res;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    // getLiquidationPrice for openable positions
    // in an ideal situation this liquidationPrice is equal getLiquidationPrice for opened positions
    function getLiquidationPrice(
        address _positionManager,
        string memory _bucket,
        uint256 _borrowedAmount,
        address _positionAsset,
        uint256 _positionAmount
    ) external view override returns (uint256) {
        _require(
            IERC165(address(_positionManager)).supportsInterface(type(IPositionManagerV2).interfaceId) &&
                _positionAsset != address(0),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        IPrimexDNSV3 primexDNS = IPositionManagerV2(_positionManager).primexDNS();
        address bucket = primexDNS.getBucketAddress(_bucket);
        return
            PrimexPricingLibrary.getLiquidationPrice(
                bucket,
                _positionAsset,
                _positionAmount,
                _borrowedAmount,
                address(primexDNS)
            );
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getLimitOrdersWithConditions(
        address _limitOrderManager,
        uint256 _cursor,
        uint256 _count
    ) external view override returns (LimitOrderWithConditions[] memory limitOrdersWithConditions, uint256 newCursor) {
        _require(
            IERC165(_limitOrderManager).supportsInterface(type(ILimitOrderManager).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        uint256 ordersLength = ILimitOrderManager(_limitOrderManager).getOrdersLength();
        if (_cursor >= ordersLength) {
            return (limitOrdersWithConditions, 0);
        }
        if (_cursor + _count >= ordersLength) {
            _count = ordersLength - _cursor;
        } else {
            newCursor = _cursor + _count;
        }
        limitOrdersWithConditions = new LimitOrderWithConditions[](_count);
        for (uint256 i; i < _count; i++) {
            limitOrdersWithConditions[i].limitOrderData = ILimitOrderManager(_limitOrderManager).getOrderByIndex(
                _cursor + i
            );
            limitOrdersWithConditions[i].openConditionsData = ILimitOrderManager(_limitOrderManager).getOpenConditions(
                limitOrdersWithConditions[i].limitOrderData.id
            );
        }
        return (limitOrdersWithConditions, newCursor);
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getTokenArrayMetadata(
        address[] calldata _tokens,
        address _trader
    ) external view override returns (TokenMetadata[] memory) {
        uint256 tokenCount = _tokens.length;
        TokenMetadata[] memory res = new TokenMetadata[](tokenCount);

        for (uint256 i; i < tokenCount; i++) {
            res[i] = getTokenMetadata(_tokens[i], _trader);
        }

        return res;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getOpenPositionData(
        address _positionManager,
        uint256 _id
    ) public view override returns (OpenPositionData memory) {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );

        PositionLibrary.Position memory position = IPositionManagerV2(_positionManager).getPosition(_id);

        bool isSpot = address(position.bucket) == address(0);
        uint256 debt = IPositionManagerV2(_positionManager).getPositionDebt(_id);
        BucketMetaData memory bucket;
        if (!isSpot) bucket = getBucket(address(position.bucket), position.trader);

        LimitOrderLibrary.Condition[] memory closeConditions = IPositionManagerV2(_positionManager).getCloseConditions(
            _id
        );
        ITakeProfitStopLossCCM.CanBeClosedParams memory params;
        for (uint256 i; i < closeConditions.length; i++) {
            if (
                IERC165(
                    IPositionManagerV2(_positionManager).primexDNS().cmTypeToAddress(closeConditions[i].managerType)
                ).supportsInterface(type(ITakeProfitStopLossCCM).interfaceId) && closeConditions[i].params.length != 0
            ) {
                params = abi.decode(closeConditions[i].params, (ITakeProfitStopLossCCM.CanBeClosedParams));
                break;
            }
        }
        return
            OpenPositionData({
                id: position.id,
                bucket: bucket,
                pair: [
                    isSpot ? getTokenMetadata(position.soldAsset, position.trader) : bucket.asset,
                    getTokenMetadata(position.positionAsset, position.trader)
                ],
                positionSize: position.positionAmount,
                liquidationPrice: getLiquidationPrice(_positionManager, _id),
                stopLossPrice: params.stopLossPrice,
                takeProfitPrice: params.takeProfitPrice,
                debt: debt,
                depositAmount: position.depositAmountInSoldAsset,
                createdAt: position.createdAt,
                extraParams: position.extraParams
            });
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function isStopLossReached(
        address _positionManager,
        uint256 _id,
        bytes calldata _positionSoldAssetOracleData,
        bytes[][] calldata _pullOracleData,
        uint256[] calldata _pullOracleTypes
    ) public payable override returns (bool) {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        IPositionManagerV2 pm = IPositionManagerV2(_positionManager);
        PositionLibrary.Position memory position = pm.getPosition(_id);
        LimitOrderLibrary.Condition[] memory closeConditions = pm.getCloseConditions(_id);
        pm.priceOracle().updatePullOracle{value: msg.value}(_pullOracleData, _pullOracleTypes);

        if (closeConditions.length == 0) return false;

        ITakeProfitStopLossCCM.CanBeClosedParams memory params;

        for (uint256 i; i < closeConditions.length; i++) {
            if (
                IERC165(pm.primexDNS().cmTypeToAddress(closeConditions[i].managerType)).supportsInterface(
                    type(ITakeProfitStopLossCCM).interfaceId
                )
            ) {
                params = abi.decode(closeConditions[i].params, (ITakeProfitStopLossCCM.CanBeClosedParams));
                break;
            }
        }
        return
            ITakeProfitStopLossCCM(takeProfitStopLossCCM).isStopLossReached(
                position,
                params.stopLossPrice,
                _positionSoldAssetOracleData
            );
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getTokenMetadata(address _token, address _trader) public view override returns (TokenMetadata memory) {
        _require(_token != address(0), Errors.ADDRESS_NOT_SUPPORTED.selector);
        return
            TokenMetadata({
                tokenAddress: _token,
                symbol: IERC20Metadata(_token).symbol(),
                name: IERC20Metadata(_token).name(),
                decimals: IERC20Metadata(_token).decimals(),
                balance: _trader != address(0) ? IERC20Metadata(_token).balanceOf(_trader) : 0
            });
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getAssetMetadata(
        address _bucket,
        address _asset
    ) public view override returns (BucketTokenMetadata memory) {
        _require(
            IERC165(_bucket).supportsInterface(type(IBucketV3).interfaceId) && _asset != address(0),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        IPositionManagerV2 pm = IBucketV3(_bucket).positionManager();
        uint256 pairPriceDrop = pm.priceOracle().pairPriceDrops(_asset, address(IBucketV3(_bucket).borrowedAsset()));

        (uint256 id, bool isSupported) = IBucketV3(_bucket).allowedAssets(_asset);
        return
            BucketTokenMetadata({
                id: id,
                isSupported: isSupported,
                pairPriceDrop: pairPriceDrop,
                // TODO: what FeeRateType should be used here?
                maxLeverage: IBucketV3(_bucket).maxAssetLeverage(
                    _asset,
                    pm.primexDNS().protocolFeeRates(IPrimexDNSStorageV3.FeeRateType.MarginPositionClosedByKeeper)
                )
            });
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getSupportedAsset(
        address _bucket,
        address _asset,
        address _trader
    ) public view override returns (SupportedAsset memory) {
        return
            SupportedAsset({asset: getTokenMetadata(_asset, _trader), properties: getAssetMetadata(_bucket, _asset)});
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getSupportedAssetArray(
        address _bucket,
        address[] memory _assets,
        address _trader
    ) public view override returns (SupportedAsset[] memory) {
        uint256 assetCount = _assets.length;
        SupportedAsset[] memory res = new SupportedAsset[](assetCount);

        for (uint256 i; i < assetCount; i++) {
            res[i] = getSupportedAsset(_bucket, _assets[i], _trader);
        }

        return res;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getBucket(address _bucket, address _user) public view override returns (BucketMetaData memory) {
        _require(
            IERC165(_bucket).supportsInterface(type(IBucketV3).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        uint256 availableLiquidity = IBucketV3(_bucket).availableLiquidity();
        uint256 demand = IBucketV3(_bucket).debtToken().totalSupply();
        uint256 supply = demand + availableLiquidity;
        uint256 ur = supply > 0 ? demand.rdiv(supply) : 0;

        address[] memory allowedAssets = IBucketV3(_bucket).getAllowedAssets();
        SupportedAsset[] memory supportedAssets = getSupportedAssetArray(_bucket, allowedAssets, _user);
        // solhint-disable-next-line var-name-mixedcase
        IBucketV3.LiquidityMiningParams memory LMparams = IBucketV3(_bucket).getLiquidityMiningParams();
        IInterestRateStrategy.BarCalculationParams memory barCalcParams = IBucketV3(_bucket)
            .interestRateStrategy()
            .getBarCalculationParams(_bucket);
        return
            BucketMetaData({
                bucketAddress: _bucket,
                name: IBucketV3(_bucket).name(),
                asset: getTokenMetadata(address(IBucketV3(_bucket).borrowedAsset()), _user),
                bar: IBucketV3(_bucket).bar(),
                lar: IBucketV3(_bucket).lar(),
                supply: supply,
                demand: demand,
                availableLiquidity: availableLiquidity,
                utilizationRatio: ur,
                supportedAssets: supportedAssets,
                pToken: getTokenMetadata(address(IBucketV3(_bucket).pToken()), _user),
                debtToken: getTokenMetadata(address(IBucketV3(_bucket).debtToken()), _user),
                feeBuffer: IBucketV3(_bucket).feeBuffer(),
                withdrawalFeeRate: IBucketV3(_bucket).withdrawalFeeRate(),
                miningParams: LMparams,
                lenderInfo: getLenderInfo(LMparams.liquidityMiningRewardDistributor, IBucketV3(_bucket).name(), _user),
                lmBucketInfo: getLMBucketInfo(LMparams.liquidityMiningRewardDistributor, IBucketV3(_bucket).name()),
                estimatedBar: IBucketV3(_bucket).estimatedBar(),
                estimatedLar: IBucketV3(_bucket).estimatedLar(),
                isDeprecated: IBucketV3(_bucket).isDeprecated(),
                isDelisted: IBucketV3(_bucket).isDelisted(),
                barCalcParams: barCalcParams,
                maxTotalDeposit: IBucketV3(_bucket).maxTotalDeposit()
            });
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getLenderInfo(
        ILiquidityMiningRewardDistributor liquidityMiningRewardDistributor,
        string memory bucketName,
        address user
    ) public view override returns (LenderInfo memory) {
        LenderInfo memory info;
        if (address(liquidityMiningRewardDistributor) == address(0)) return info;
        (info.amountInMining, info.currentPercent, info.rewardsInPMX) = liquidityMiningRewardDistributor.getLenderInfo(
            bucketName,
            user,
            block.timestamp
        );
        return info;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getLMBucketInfo(
        ILiquidityMiningRewardDistributor liquidityMiningRewardDistributor,
        string memory bucketName
    ) public view override returns (LiquidityMiningBucketInfo memory) {
        LiquidityMiningBucketInfo memory info;
        if (address(liquidityMiningRewardDistributor) == address(0)) return info;
        (info.pmxAmount, info.withdrawnRewards, info.totalPoints) = liquidityMiningRewardDistributor.getBucketInfo(
            bucketName
        );
        return info;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getBucketsArray(
        address[] memory _buckets,
        address _user,
        address _positionManager,
        bool _showDeprecated
    ) public view override returns (BucketMetaData[] memory) {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        uint256 bucketCount;
        for (uint256 i; i < _buckets.length; i++) {
            IBucketV3 bucket = IBucketV3(_buckets[i]);
            (address bucketAddress, IPrimexDNSStorage.Status currentStatus, , ) = IPositionManagerV2(_positionManager)
                .primexDNS()
                .buckets(bucket.name());
            if (
                (_showDeprecated ||
                    !(currentStatus == IPrimexDNSStorage.Status.Deprecated && bucket.pToken().balanceOf(_user) == 0)) &&
                bucketAddress == _buckets[i]
            ) {
                _buckets[bucketCount] = _buckets[i];
                bucketCount++;
            }
        }
        BucketMetaData[] memory res = new BucketMetaData[](bucketCount);
        for (uint256 i; i < bucketCount; i++) {
            res[i] = getBucket(_buckets[i], _user);
        }
        return res;
    }

    /**
     * @notice Interface checker
     * @param interfaceId The interface id to check
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(IPrimexLens).interfaceId || interfaceId == type(IERC165).interfaceId;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getLiquidationPrice(address _positionManager, uint256 _id) public view override returns (uint256) {
        _require(
            IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );

        PositionLibrary.Position memory position = IPositionManagerV2(_positionManager).getPosition(_id);
        if (position.scaledDebtAmount == 0) return 0;

        uint256 positionDebt = IPositionManagerV2(_positionManager).getPositionDebt(_id);
        return
            PrimexPricingLibrary.getLiquidationPrice(
                address(position.bucket),
                position.positionAsset,
                position.positionAmount,
                positionDebt,
                address(IPositionManagerV2(_positionManager).primexDNS())
            );
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getPositionMaxDecrease(
        IPositionManagerV2 _pm,
        uint256 _id,
        bytes calldata _positionSoldAssetOracleData,
        bytes[][] calldata _pullOracleData,
        uint256[] calldata _pullOracleTypes
    ) public payable override returns (uint256) {
        _pm.priceOracle().updatePullOracle{value: msg.value}(_pullOracleData, _pullOracleTypes);
        PositionLibrary.Position memory position = _pm.getPosition(_id);
        uint256 pairPriceDrop = _pm.priceOracle().getPairPriceDrop(position.positionAsset, address(position.soldAsset));
        uint256 securityBuffer = _pm.securityBuffer();
        uint256 maintenanceBuffer = _pm.maintenanceBuffer();

        uint256 oracleTolerableLimit = _pm.getOracleTolerableLimit(position.positionAsset, address(position.soldAsset));

        uint256 feeBuffer = position.bucket.feeBuffer();
        uint256 positionAmountInBorrowedAsset = PrimexPricingLibrary.getOracleAmountsOut(
            position.positionAsset,
            position.soldAsset,
            position.positionAmount,
            address(_pm.priceOracle()),
            _positionSoldAssetOracleData
        );
        uint256 maxDecrease = (WadRayMath.WAD - securityBuffer)
            .wmul(WadRayMath.WAD - oracleTolerableLimit)
            .wmul(WadRayMath.WAD - pairPriceDrop)
            .wmul(positionAmountInBorrowedAsset)
            .wdiv(feeBuffer.wmul(WadRayMath.WAD + maintenanceBuffer)) -
            position.bucket.getNormalizedVariableDebt().rmul(position.scaledDebtAmount);

        return maxDecrease <= position.depositAmountInSoldAsset ? maxDecrease : position.depositAmountInSoldAsset;
    }

    /**
     * @inheritdoc IPrimexLens
     */
    function getEstimatedMinProtocolFee(
        IPrimexDNSStorageV3.TradingOrderType _tradingOrderType,
        IPositionManagerV2 _pm
    ) public view override returns (uint256) {
        uint256 restrictedGasPrice = PrimexPricingLibrary.calculateRestrictedGasPrice(
            address(_pm.priceOracle()),
            _pm.keeperRewardDistributor()
        );
        (, , uint256 optimisticGasCoefficient, IKeeperRewardDistributorStorage.PaymentModel paymentModel) = _pm
            .keeperRewardDistributor()
            .getGasCalculationParams();
        uint256 l1CostWei;
        if (paymentModel != IKeeperRewardDistributorStorage.PaymentModel.DEFAULT) {
            if (paymentModel == IKeeperRewardDistributorStorage.PaymentModel.ARBITRUM) {
                l1CostWei =
                    ARB_NITRO_ORACLE.getL1BaseFeeEstimate() *
                    GAS_FOR_BYTE *
                    (_pm.primexDNS().getL1BaseLengthForTradingOrderType(_tradingOrderType) +
                        TRANSACTION_METADATA_BYTES);
            }
            if (paymentModel == IKeeperRewardDistributorStorage.PaymentModel.OPTIMISTIC) {
                // Adds 68 bytes of padding to account for the fact that the input does not have a signature.
                uint256 l1GasUsed = GAS_FOR_BYTE *
                    (_pm.primexDNS().getL1BaseLengthForTradingOrderType(_tradingOrderType) +
                        OVM_GASPRICEORACLE.overhead() +
                        68);
                l1CostWei =
                    (OVM_GASPRICEORACLE.l1BaseFee() *
                        l1GasUsed *
                        OVM_GASPRICEORACLE.scalar() *
                        optimisticGasCoefficient) /
                    10 ** 6;
            }
        }
        uint256 estimatedMinProtocolFeeInNativeAsset = _pm.primexDNS().averageGasPerAction(_tradingOrderType) *
            restrictedGasPrice +
            l1CostWei;
        return estimatedMinProtocolFeeInNativeAsset;
    }
}

