// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {ERC165Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";

import {IPriceOracleStorage, IPriceOracleStorageV2, IPriceOracleStorageV3} from "./IPriceOracleStorage.sol";

import {IPyth} from "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import {PythStructs} from "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";
import {ISupraOraclePull} from "../interfaces/ISupraOraclePull.sol";
import {ISupraSValueFeed} from "../interfaces/ISupraSValueFeed.sol";

abstract contract PriceOracleStorage is IPriceOracleStorage, ERC165Upgradeable {
    address public override registry;
    address public override eth;
    address public override gasPriceFeed;

    // PriceDrop of asset A relative to asset B
    mapping(address => mapping(address => uint256)) public override pairPriceDrops;

    /**
     * @notice The mapping stores priceDrop feeds adresses associated with the asset pair.
     * @dev 'The address of the first asset in the pair' -> ' The address of the second asset in the pair' -> 'priceDrop feed address'
     */
    mapping(address => mapping(address => address)) internal oraclePriceDropFeeds;

    /**
     * @notice The mapping stores price feeds for currency pairs.
     * @dev 'base currency address' -> 'quote currency address' -> 'price feed address'
     */
    mapping(address => mapping(address => address)) internal chainLinkPriceFeeds;
}

abstract contract PriceOracleStorageV2 is IPriceOracleStorageV2, PriceOracleStorage {
    IPyth public override pyth;
    uint256 public override timeTolerance;
    // baseToken => chainlink usd price feed;
    mapping(address => address) public override chainlinkPriceFeedsUsd;

    // baseToken => the pyth ID of the price feed to get an update for
    mapping(address => bytes32) public override pythPairIds;

    // oracleType => corresponding oracle address
    mapping(uint256 => address) public override univ3TypeOracles;

    // univ3TypeOracles => tokenA => tokenB
    mapping(uint256 => mapping(address => mapping(address => bool))) public override univ3TrustedPairs;
}

abstract contract PriceOracleStorageV3 is IPriceOracleStorageV3, PriceOracleStorageV2 {
    ISupraOraclePull public override supraPullOracle;
    ISupraSValueFeed public override supraStorageOracle;

    // assetA => assetB => feedId
    mapping(address => mapping(address => SupraDataFeedId)) public override supraDataFeedID;
    address public override usdt;
    address public override treasury;
}

contracts/PrimexDNS/IPrimexDNS.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.18;

import {IPrimexDNSStorage, IPrimexDNSStorageV3} from "./IPrimexDNSStorage.sol";

interface IPrimexDNSV3 is IPrimexDNSStorageV3 {
    event AddNewBucket(BucketData newBucketData);
    event BucketDeprecated(address bucketAddress, uint256 delistingTime);
    event AddNewDex(DexData newDexData);
    event ConditionalManagerChanged(uint256 indexed cmType, address indexed cmAddress);
    event PMXchanged(address indexed pmx);
    event AavePoolChanged(address indexed aavePool);
    event BucketActivated(address indexed bucketAddress);
    event BucketFrozen(address indexed bucketAddress);
    event DexAdapterChanged(address indexed newAdapterAddress);
    event DexActivated(address indexed routerAddress);
    event DexFrozen(address indexed routerAddress);

    event ChangeProtocolFeeRate(FeeRateType indexed feeRateType, uint256 indexed feeRate);
    event ChangeAverageGasPerAction(TradingOrderType indexed tradingOrderType, uint256 indexed averageGasPerAction);
    event ChangeMaxProtocolFee(uint256 indexed maxProtocolFee);
    event ChangeProtocolFeeCoefficient(uint256 indexed protocolFeeCoefficient);
    event ChangeLiquidationGasAmount(uint256 indexed liquidationGasAmount);
    event ChangePmxDiscountMultiplier(uint256 indexed pmxDiscountMultiplier);
    event ChangeAdditionalGasSpent(uint256 indexed additionalGasSpent);
    event ChangeGasPriceBuffer(uint256 indexed gasPriceBuffer);
    event ChangeMinFeeRestrictions(CallingMethod indexed callingMethod, MinFeeRestrictions minFeeRestrictions);
    event ChangeLeverageTolerance(uint256 leverageTolerance);

    /**
     * @param feeRateType The order type for which the rate is set
     * @param feeRate Setting rate in WAD format (1 WAD = 100%)
     */
    struct FeeRateParams {
        FeeRateType feeRateType;
        uint256 feeRate;
    }

    struct AverageGasPerActionParams {
        TradingOrderType tradingOrderType;
        uint256 averageGasPerAction;
    }

    /**
     * @dev Params for initialize() function
     * @param registry The address of the PrimexRegistry contract.
     * @param pmx The address of the PMX token contract.
     * @param treasury The address of the Treasury contract.
     * @param delistingDelay The time (in seconds) between deprecation and delisting of a bucket.
     * @param adminWithdrawalDelay The time (in seconds) between delisting of a bucket and an adminDeadline.
     * @param feeRateParams An array of structs to set protocol fee rate on the corresponding
     * @param averageGasPerActionParams An array of structs to set average amount of gas spent by Keeper on the corresponding action
     * @param maxProtocolFee MaxProtocolFee that can be charged. Measured in NATIVE_CURRENCY
     * @param liquidationGasAmount Average gas amount spent for a single liquidation, measured in wei.
     * @param protocolFeeCoefficient Additional coefficient to calculate minProtocolFee, measured in wei.
     * @param additionalGasSpent Gas that will be additionally spend after gasSpent calculation.
     * @param pmxDiscountMultiplier Multiplier for PMX discount calculation
     * @param gasPriceBuffer Multiplier which protects position from immediate liquidation after gas price changed
     */
    struct InitParams {
        address registry;
        address pmx;
        address treasury;
        uint256 delistingDelay;
        uint256 adminWithdrawalDelay;
        FeeRateParams[] feeRateParams;
        AverageGasPerActionParams[] averageGasPerActionParams;
        uint256 maxProtocolFee;
        uint256 liquidationGasAmount;
        uint256 protocolFeeCoefficient;
        uint256 additionalGasSpent;
        uint256 pmxDiscountMultiplier;
        uint256 gasPriceBuffer;
        uint256 leverageTolerance;
    }

    /**
     * @notice Initializes the contract with the specified parameters.
     */
    function initialize(InitParams calldata _params) external;

    /**
     * @notice Deprecates a bucket.
     * @dev This function is used to deprecate a bucket by changing its current status to "Deprecated".
     * Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _bucket The name of the bucket to deprecate.
     * Emits a BucketDeprecated event with the bucket address and the delisting time.
     */
    function deprecateBucket(string memory _bucket) external;

    /**
     * @notice This function is used to set the address of the Aave pool contract.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _aavePool The address of the Aave pool contract to be set.
     */
    function setAavePool(address _aavePool) external;

    /**
     * @notice Sets the address of the PMX token contract.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _pmx The address of the PMX token contract.
     */
    function setPMX(address _pmx) external;

    /**
     * @notice Activates a bucket by changing its status from inactive to active.
     * @dev Only callable by the SMALL_TIMELOCK_ADMIN role.
     * @param _bucket The bucket to activate.
     */
    function activateBucket(string memory _bucket) external;

    /**
     * @notice Freezes a bucket, preventing further operations on it,
     * by changing its status from active to inactive.
     * @dev Only callable by the EMERGENCY_ADMIN role.
     * @param _bucket The bucket to be frozen.
     */
    function freezeBucket(string memory _bucket) external;

    /**
     * @notice Adds a new bucket.
     * @dev Only callable by the SMALL_TIMELOCK_ADMIN role.
     * @param _newBucket The address of the new bucket to be added.
     * @param _pmxRewardAmount The amount of PMX tokens to be rewarded from the bucket.
     * Emits a AddNewBucket event with the struct BucketData of the newly added bucket.
     */
    function addBucket(address _newBucket, uint256 _pmxRewardAmount) external;

    /**
     * @notice Activates a DEX by changing flag isActive on to true.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     * @param _dex The name of the DEX to activate.
     */
    function activateDEX(string memory _dex) external;

    /**
     * @notice Freezes a DEX by changing flag isActive to false.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     * @param _dex The name of the DEX to be frozen.
     */
    function freezeDEX(string memory _dex) external;

    /**
     * @notice Adds a new DEX to the protocol.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     * @param _name The name of the DEX.
     * @param _routerAddress The address of the DEX router.
     */
    function addDEX(string memory _name, address _routerAddress) external;

    /**
     * @notice Sets the address of the DEX adapter.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param newAdapterAddress The address of the new DEX adapter.
     */
    function setDexAdapter(address newAdapterAddress) external;

    /**
     * @notice Set min protocol fee restrictions for different calling method.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     */
    function setMinFeeRestrictions(
        CallingMethod _callingMethod,
        MinFeeRestrictions calldata _minFeeRestrictions
    ) external;

    /**
     * @dev The function to specify the address of conditional manager of some type
     * 1 => LimitPriceCOM
     * 2 => TakeProfitStopLossCCM
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _address Address to be set for a conditional manager
     * @param _cmType The type of a conditional manager
     */
    function setConditionalManager(uint256 _cmType, address _address) external;

    /**
     * @notice Retrieves the address of a bucket by its name.
     * @param _name The name of the bucket.
     * @return The address of the bucket.
     */
    function getBucketAddress(string memory _name) external view returns (address);

    /**
     * @notice Retrieves the address of the DEX router based on the given DEX name.
     * @param _name The name of the DEX.
     * @return The address of the DEX router.
     */
    function getDexAddress(string memory _name) external view returns (address);

    /**
     * @notice Retrieves the names of Dexes registered in the protocol.
     * @return An array of strings containing the names of all Dexes.
     */
    function getAllDexes() external view returns (string[] memory);

    /**
     * @notice Set the protocol fee rate for one type of order.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     */
    function setProtocolFeeRate(FeeRateParams calldata _feeRateType) external;

    /**
     * @notice Set average gas amount of gas spent by Keeper on the corresponding action.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     */
    function setAverageGasPerAction(AverageGasPerActionParams calldata _averageGasPerActionParams) external;

    /**
     * @notice Set the max protocol fee.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _maxProtocolFee The new max protocol fee.
     */
    function setMaxProtocolFee(uint256 _maxProtocolFee) external;

    /**
     * @notice Set protocol fee coefficient. Used to calculate the minProtocol fee
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     */
    function setProtocolFeeCoefficient(uint256 _maxProtocolFee) external;

    /**
     * @notice Set liquidation gas amount (average gas amount spent for a single liquidation).
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     */
    function setLiquidationGasAmount(uint256 _maxProtocolFee) external;

    /**
     * @notice Set pmxDiscountMultiplier.
     * @dev Only callable by the SMALL_TIMELOCK_ADMIN role.
     */
    function setPmxDiscountMultiplier(uint256 _pmxDiscountMultiplier) external;

    /**
     * @notice Set new additionalGas. Used to calculate the minProtocol fee
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     */
    function setAdditionalGasSpent(uint256 _additionalGasSpent) external;

    /**
     * @notice Set new gasPriceBuffer.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     */
    function setGasPriceBuffer(uint256 _gasPriceBuffer) external;

    /**
     * @notice Set new leverageTolerance.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     */
    function setLeverageTolerance(uint256 _leverageTolerance) external;

    /**
     * @notice Retrieves pmx, treasury, feeRateType, maxProtocolFee, pmxDiscountMultiplier
     */
    function getPrimexDNSParams(
        FeeRateType _feeRateType
    ) external view returns (address, address, uint256, uint256, uint256);

    /**
     * @notice Retrieves liquidationGasAmount, protocolFeeCoefficient, additionalGasSpent, maxGasAmount and baseLength
     */
    function getParamsForMinProtocolFee(
        CallingMethod _callingMethod
    ) external view returns (uint256, uint256, uint256, uint256, uint256);

    /**
     * @notice Retrieves baseLength, averageGasPerAction, protocolFeeCoefficient and gasPriceBuffer
     */
    function getParamsForMinPositionSize(
        TradingOrderType _tradingOrderType
    ) external view returns (uint256, uint256, uint256, uint256);

    /**
     * @notice Retrieves baseLength for L2 chain payment model depending from tradingOrderType
     */
    function getL1BaseLengthForTradingOrderType(TradingOrderType _tradingOrderType) external view returns (uint256);
}

interface IPrimexDNS is IPrimexDNSStorage {
    event AddNewBucket(BucketData newBucketData);
    event BucketDeprecated(address bucketAddress, uint256 delistingTime);
    event AddNewDex(DexData newDexData);
    event ChangeFeeRate(OrderType orderType, address token, uint256 rate);
    event ConditionalManagerChanged(uint256 indexed cmType, address indexed cmAddress);
    event PMXchanged(address indexed pmx);
    event AavePoolChanged(address indexed aavePool);
    event BucketActivated(address indexed bucketAddress);
    event BucketFrozen(address indexed bucketAddress);
    event DexAdapterChanged(address indexed newAdapterAddress);
    event DexActivated(address indexed routerAddress);
    event DexFrozen(address indexed routerAddress);

    /**
     * @param orderType The order type for which the rate is set
     * @param feeToken The token address for which the rate is set
     * @param rate Setting rate in WAD format (1 WAD = 100%)
     */
    struct FeeRateParams {
        OrderType orderType;
        address feeToken;
        uint256 rate;
    }

    /**
     * @notice Initializes the contract with the specified parameters.
     * @param _registry The address of the PrimexRegistry contract.
     * @param _pmx The address of the PMX token contract.
     * @param _treasury The address of the Treasury contract.
     * @param _delistingDelay The time (in seconds) between deprecation and delisting of a bucket.
     * @param _adminWithdrawalDelay The time (in seconds) between delisting of a bucket and an adminDeadline.
     * @param _feeRateParams Initial fee params
     */
    function initialize(
        address _registry,
        address _pmx,
        address _treasury,
        uint256 _delistingDelay,
        uint256 _adminWithdrawalDelay,
        FeeRateParams[] calldata _feeRateParams
    ) external;

    /**
     * @notice Deprecates a bucket.
     * @dev This function is used to deprecate a bucket by changing its current status to "Deprecated".
     * Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _bucket The name of the bucket to deprecate.
     * Emits a BucketDeprecated event with the bucket address and the delisting time.
     */
    function deprecateBucket(string memory _bucket) external;

    /**
     * @notice This function is used to set the address of the Aave pool contract.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _aavePool The address of the Aave pool contract to be set.
     */
    function setAavePool(address _aavePool) external;

    /**
     * @notice Sets the protocol rate in PMX.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     */
    function setFeeRate(FeeRateParams calldata _feeRateParams) external;

    /**
     * @notice Sets the address of the PMX token contract.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _pmx The address of the PMX token contract.
     */
    function setPMX(address _pmx) external;

    /**
     * @notice Activates a bucket by changing its status from inactive to active.
     * @dev Only callable by the SMALL_TIMELOCK_ADMIN role.
     * @param _bucket The bucket to activate.
     */
    function activateBucket(string memory _bucket) external;

    /**
     * @notice Freezes a bucket, preventing further operations on it,
     * by changing its status from active to inactive.
     * @dev Only callable by the EMERGENCY_ADMIN role.
     * @param _bucket The bucket to be frozen.
     */
    function freezeBucket(string memory _bucket) external;

    /**
     * @notice Adds a new bucket.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param _newBucket The address of the new bucket to be added.
     * @param _pmxRewardAmount The amount of PMX tokens to be rewarded from the bucket.
     * Emits a AddNewBucket event with the struct BucketData of the newly added bucket.
     */
    function addBucket(address _newBucket, uint256 _pmxRewardAmount) external;

    /**
     * @notice Activates a DEX by changing flag isActive on to true.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     * @param _dex The name of the DEX to activate.
     */
    function activateDEX(string memory _dex) external;

    /**
     * @notice Freezes a DEX by changing flag isActive to false.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     * @param _dex The name of the DEX to be frozen.
     */
    function freezeDEX(string memory _dex) external;

    /**
     * @notice Adds a new DEX to the protocol.
     * @dev Only callable by the MEDIUM_TIMELOCK_ADMIN role.
     * @param _name The name of the DEX.
     * @param _routerAddress The address of the DEX router.
     */
    function addDEX(string memory _name, address _routerAddress) external;

    /**
     * @notice Sets the address of the DEX adapter.
     * @dev Only callable by the BIG_TIMELOCK_ADMIN role.
     * @param newAdapterAddress The address of the new DEX adapter.
     */
    function setDexAdapter(address newAdapterAddress) external;

    /**
     * @dev The function to specify the address of conditional manager of some type
     * 1 => LimitPriceCOM
     * 2 => TakeProfitStopLossCCM
     * @param _address Address to be set for a conditional manager
     * @param _cmType The type of a conditional manager
     */
    function setConditionalManager(uint256 _cmType, address _address) external;

    /**
     * @notice Retrieves the address of a bucket by its name.
     * @param _name The name of the bucket.
     * @return The address of the bucket.
     */
    function getBucketAddress(string memory _name) external view returns (address);

    /**
     * @notice Retrieves the address of the DEX router based on the given DEX name.
     * @param _name The name of the DEX.
     * @return The address of the DEX router.
     */
    function getDexAddress(string memory _name) external view returns (address);

    /**
     * @notice Retrieves the names of Dexes registered in the protocol.
     * @return An array of strings containing the names of all Dexes.
     */
    function getAllDexes() external view returns (string[] memory);
}

contracts/PrimexDNS/IPrimexDNSStorage.so.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.18;

interface IPrimexDNSStorage {
    enum Status {
        Inactive,
        Active,
        Deprecated
    }

    enum OrderType {
        MARKET_ORDER,
        LIMIT_ORDER,
        SWAP_MARKET_ORDER,
        SWAP_LIMIT_ORDER
    }

    struct BucketData {
        address bucketAddress;
        Status currentStatus;
        uint256 delistingDeadline;
        // The deadline is for the admin to call Bucket.withdrawAfterDelisting().
        uint256 adminDeadline;
    }
    struct DexData {
        address routerAddress;
        bool isActive;
    }

    struct AdapterData {
        string[] dexes;
        bool isAdded;
    }

    function registry() external view returns (address);

    function delistingDelay() external view returns (uint256);

    function adminWithdrawalDelay() external view returns (uint256);

    function buckets(string memory) external view returns (address, Status, uint256, uint256);

    function dexes(string memory) external view returns (address, bool);

    function cmTypeToAddress(uint256 cmType) external view returns (address);

    function dexAdapter() external view returns (address);

    function pmx() external view returns (address);

    function treasury() external view returns (address);

    function aavePool() external view returns (address);

    function feeRates(OrderType _orderType, address _token) external view returns (uint256);
}

interface IPrimexDNSStorageV2 is IPrimexDNSStorage {
    struct FeeRestrictions {
        uint256 minProtocolFee;
        uint256 maxProtocolFee;
    }

    function feeRestrictions(
        OrderType _orderType
    ) external view returns (uint256 minProtocolFee, uint256 maxProtocolFee);
}

interface IPrimexDNSStorageV3 is IPrimexDNSStorageV2 {
    enum FeeRateType {
        MarginPositionClosedByTrader,
        SpotPositionClosedByTrader,
        MarginPositionClosedByKeeper,
        SpotPositionClosedByKeeper,
        MarginLimitOrderExecuted,
        SpotLimitOrderExecuted,
        SwapLimitOrderExecuted,
        SwapMarketOrder
    }

    enum TradingOrderType {
        MarginMarketOrder,
        SpotMarketOrder,
        MarginLimitOrder,
        MarginLimitOrderDepositInThirdAsset,
        SpotLimitOrder,
        SwapLimitOrder
    }

    enum CallingMethod {
        OpenPositionByOrder,
        ClosePositionByCondition
    }
    struct MinFeeRestrictions {
        uint256 maxGasAmount;
        uint256 baseLength;
    }

    function protocolFeeRates(FeeRateType _feeRateType) external view returns (uint256);

    function averageGasPerAction(TradingOrderType _tradingOrderType) external view returns (uint256);

    function minFeeRestrictions(
        CallingMethod _callingMethod
    ) external view returns (uint256 maxGasAmount, uint256 baseLength);

    function maxProtocolFee() external view returns (uint256);

    function protocolFeeCoefficient() external view returns (uint256);

    function liquidationGasAmount() external view returns (uint256);

    function additionalGasSpent() external view returns (uint256);

    function pmxDiscountMultiplier() external view returns (uint256);

    function gasPriceBuffer() external view returns (uint256);

    function leverageTolerance() external view returns (uint256);
}

contracts/PrimexDNS/PrimexDNS.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {IERC165Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/introspection/IERC165Upgradeable.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";

import "../libraries/Errors.sol";

import "./PrimexDNSStorage.sol";
import {BIG_TIMELOCK_ADMIN, MEDIUM_TIMELOCK_ADMIN, SMALL_TIMELOCK_ADMIN, EMERGENCY_ADMIN} from "../Constants.sol";
import {IBucketV3} from "../Bucket/IBucket.sol";
import {WadRayMath} from "../libraries/utils/WadRayMath.sol";
import {IPrimexDNSV3, IPrimexDNS} from "./IPrimexDNS.sol";
import {IDexAdapter} from "../interfaces/IDexAdapter.sol";
import {ILiquidityMiningRewardDistributor} from "../LiquidityMiningRewardDistributor/ILiquidityMiningRewardDistributor.sol";
import {ITreasury} from "../Treasury/ITreasury.sol";
import {IConditionalClosingManager} from "../interfaces/IConditionalClosingManager.sol";
import {IConditionalOpeningManager} from "../interfaces/IConditionalOpeningManager.sol";

contract PrimexDNS is IPrimexDNSV3, PrimexDNSStorageV3 {
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Modifier that checks if the caller has a specific role.
     * @param _role The role identifier to check.
     */
    modifier onlyRole(bytes32 _role) {
        _require(IAccessControl(registry).hasRole(_role, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function initialize(InitParams calldata _params) public override initializer {
        _require(
            IERC165Upgradeable(_params.pmx).supportsInterface(type(IERC20).interfaceId) &&
                IERC165Upgradeable(_params.registry).supportsInterface(type(IAccessControl).interfaceId) &&
                IERC165Upgradeable(_params.treasury).supportsInterface(type(ITreasury).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        _setMaxProtocolFee(_params.maxProtocolFee);
        _setLiquidationGasAmount(_params.liquidationGasAmount);
        _setProtocolFeeCoefficient(_params.protocolFeeCoefficient);
        _setAdditionalGasSpent(_params.additionalGasSpent);
        _setPmxDiscountMultiplier(_params.pmxDiscountMultiplier);
        _setGasPriceBuffer(_params.gasPriceBuffer);
        _setLeverageTolerance(_params.leverageTolerance);

        for (uint256 i; i < _params.feeRateParams.length; i++) {
            _setProtocolFeeRate(_params.feeRateParams[i]);
        }

        for (uint256 i; i < _params.averageGasPerActionParams.length; i++) {
            _setAverageGasPerAction(_params.averageGasPerActionParams[i]);
        }

        pmx = _params.pmx;
        registry = _params.registry;
        treasury = _params.treasury;
        delistingDelay = _params.delistingDelay;
        adminWithdrawalDelay = _params.adminWithdrawalDelay;
        __ERC165_init();
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setConditionalManager(uint256 _cmType, address _address) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _require(
            IERC165Upgradeable(_address).supportsInterface(type(IConditionalOpeningManager).interfaceId) ||
                IERC165Upgradeable(_address).supportsInterface(type(IConditionalClosingManager).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        cmTypeToAddress[_cmType] = _address;
        emit ConditionalManagerChanged(_cmType, _address);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setPMX(address _pmx) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _require(
            IERC165Upgradeable(_pmx).supportsInterface(type(IERC20).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        pmx = _pmx;
        emit PMXchanged(_pmx);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setAavePool(address _aavePool) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        aavePool = _aavePool;
        emit AavePoolChanged(_aavePool);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setMaxProtocolFee(uint256 _maxProtocolFee) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _setMaxProtocolFee(_maxProtocolFee);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setMinFeeRestrictions(
        CallingMethod _callingMethod,
        MinFeeRestrictions calldata _minFeeRestrictions
    ) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        minFeeRestrictions[_callingMethod] = _minFeeRestrictions;
        emit ChangeMinFeeRestrictions(_callingMethod, _minFeeRestrictions);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setLiquidationGasAmount(uint256 _liquidationGasAmount) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _setLiquidationGasAmount(_liquidationGasAmount);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setProtocolFeeRate(FeeRateParams calldata _feeRateParams) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _setProtocolFeeRate(_feeRateParams);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setAverageGasPerAction(
        AverageGasPerActionParams calldata _averageGasPerActionParams
    ) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _setAverageGasPerAction(_averageGasPerActionParams);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setProtocolFeeCoefficient(uint256 _protocolFeeCoefficient) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _setProtocolFeeCoefficient(_protocolFeeCoefficient);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setAdditionalGasSpent(uint256 _additionalGasSpent) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _setAdditionalGasSpent(_additionalGasSpent);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setPmxDiscountMultiplier(uint256 _pmxDiscountMultiplier) external override onlyRole(SMALL_TIMELOCK_ADMIN) {
        _setPmxDiscountMultiplier(_pmxDiscountMultiplier);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setGasPriceBuffer(uint256 _gasPriceBuffer) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _setGasPriceBuffer(_gasPriceBuffer);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setLeverageTolerance(uint256 _leverageTolerance) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _setLeverageTolerance(_leverageTolerance);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getPrimexDNSParams(
        FeeRateType _feeRateType
    ) external view override returns (address, address, uint256, uint256, uint256) {
        return (pmx, treasury, protocolFeeRates[_feeRateType], maxProtocolFee, pmxDiscountMultiplier);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getParamsForMinProtocolFee(
        CallingMethod _callingMethod
    ) external view override returns (uint256, uint256, uint256, uint256, uint256) {
        MinFeeRestrictions memory restrictions = minFeeRestrictions[_callingMethod];
        return (
            liquidationGasAmount,
            protocolFeeCoefficient,
            additionalGasSpent,
            restrictions.maxGasAmount,
            restrictions.baseLength
        );
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getParamsForMinPositionSize(
        TradingOrderType _tradingOrderType
    ) external view override returns (uint256, uint256, uint256, uint256) {
        uint256 baseLength = this.getL1BaseLengthForTradingOrderType(_tradingOrderType);
        return (baseLength, averageGasPerAction[_tradingOrderType], protocolFeeCoefficient, gasPriceBuffer);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getL1BaseLengthForTradingOrderType(
        TradingOrderType _tradingOrderType
    ) external view override returns (uint256) {
        if (
            _tradingOrderType == TradingOrderType.MarginMarketOrder ||
            _tradingOrderType == TradingOrderType.SpotMarketOrder
        ) {
            return minFeeRestrictions[CallingMethod.ClosePositionByCondition].baseLength;
        } else {
            return minFeeRestrictions[CallingMethod.OpenPositionByOrder].baseLength;
        }
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function deprecateBucket(string memory _bucket) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        BucketData storage bucket = buckets[_bucket];
        _require(bucket.currentStatus != Status.Deprecated, Errors.BUCKET_IS_ALREADY_DEPRECATED.selector);
        bucket.currentStatus = Status.Deprecated;
        uint256 delistingDeadline = block.timestamp + delistingDelay;
        bucket.delistingDeadline = delistingDeadline;
        bucket.adminDeadline = delistingDeadline + adminWithdrawalDelay;
        emit BucketDeprecated(bucket.bucketAddress, delistingDeadline);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function activateBucket(string memory _bucket) external override onlyRole(SMALL_TIMELOCK_ADMIN) {
        BucketData storage bucket = buckets[_bucket];
        _require(bucket.currentStatus == Status.Inactive, Errors.BUCKET_ALREADY_ACTIVATED.selector);
        _require(bucket.bucketAddress != address(0), Errors.BUCKET_NOT_ADDED.selector);
        bucket.currentStatus = Status.Active;
        emit BucketActivated(bucket.bucketAddress);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function freezeBucket(string memory _bucket) external override onlyRole(EMERGENCY_ADMIN) {
        _require(buckets[_bucket].currentStatus == Status.Active, Errors.BUCKET_ALREADY_FROZEN.selector);
        buckets[_bucket].currentStatus = Status.Inactive;
        emit BucketFrozen(buckets[_bucket].bucketAddress);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function addBucket(address _newBucket, uint256 _pmxRewardAmount) external override onlyRole(SMALL_TIMELOCK_ADMIN) {
        _require(
            IERC165Upgradeable(_newBucket).supportsInterface(type(IBucketV3).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        string memory name = IBucketV3(_newBucket).name();
        _require(buckets[name].bucketAddress == address(0), Errors.BUCKET_IS_ALREADY_ADDED.selector);

        IBucketV3.LiquidityMiningParams memory params = IBucketV3(_newBucket).getLiquidityMiningParams();
        if (params.accumulatingAmount != 0) {
            // can be changed on transferAsset in traderBalanceVault to moving untransferable token
            IERC20(pmx).transferFrom(msg.sender, address(params.liquidityMiningRewardDistributor), _pmxRewardAmount);
            params.liquidityMiningRewardDistributor.updateBucketReward(name, _pmxRewardAmount);
        }

        BucketData memory newBucket = BucketData(_newBucket, Status.Active, 0, 0);
        buckets[name] = newBucket;
        emit AddNewBucket(newBucket);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function activateDEX(string memory _dex) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _require(!dexes[_dex].isActive, Errors.DEX_IS_ALREADY_ACTIVATED.selector);
        dexes[_dex].isActive = true;
        emit DexActivated(dexes[_dex].routerAddress);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function freezeDEX(string memory _dex) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _require(dexes[_dex].isActive, Errors.DEX_IS_ALREADY_FROZEN.selector);
        dexes[_dex].isActive = false;
        emit DexFrozen(dexes[_dex].routerAddress);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function addDEX(string memory _name, address _routerAddress) external override onlyRole(MEDIUM_TIMELOCK_ADMIN) {
        _require(dexes[_name].routerAddress == address(0), Errors.DEX_IS_ALREADY_ADDED.selector);
        _require(_routerAddress != address(0), Errors.CAN_NOT_ADD_WITH_ZERO_ADDRESS.selector);
        dexesNames.push(_name);
        DexData memory newDEX = DexData(_routerAddress, true);
        dexes[_name] = newDEX;
        emit AddNewDex(newDEX);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function setDexAdapter(address _newAdapterAddress) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _require(
            IERC165Upgradeable(_newAdapterAddress).supportsInterface(type(IDexAdapter).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        dexAdapter = _newAdapterAddress;
        emit DexAdapterChanged(_newAdapterAddress);
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getAllDexes() external view override returns (string[] memory) {
        return dexesNames;
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getBucketAddress(string memory _name) external view override returns (address) {
        BucketData memory bucket = buckets[_name];
        _require(bucket.bucketAddress != address(0), Errors.BUCKET_NOT_ADDED.selector);
        _require(bucket.currentStatus == Status.Active, Errors.BUCKET_IS_INACTIVE.selector);
        return bucket.bucketAddress;
    }

    /**
     * @inheritdoc IPrimexDNSV3
     */
    function getDexAddress(string memory _name) external view override returns (address) {
        DexData memory dex = dexes[_name];
        return dex.routerAddress;
    }

    /**
     * @notice Interface checker
     * @param interfaceId The interface id to check
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IPrimexDNSV3).interfaceId ||
            interfaceId == type(IPrimexDNS).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _setMaxProtocolFee(uint256 _maxProtocolFee) internal {
        maxProtocolFee = _maxProtocolFee;
        emit ChangeMaxProtocolFee(_maxProtocolFee);
    }

    function _setLiquidationGasAmount(uint256 _liquidationGasAmount) internal {
        liquidationGasAmount = _liquidationGasAmount;
        emit ChangeLiquidationGasAmount(_liquidationGasAmount);
    }

    function _setProtocolFeeCoefficient(uint256 _protocolFeeCoefficient) internal {
        protocolFeeCoefficient = _protocolFeeCoefficient;
        emit ChangeProtocolFeeCoefficient(_protocolFeeCoefficient);
    }

    function _setProtocolFeeRate(FeeRateParams calldata _feeRateParams) internal {
        protocolFeeRates[_feeRateParams.feeRateType] = _feeRateParams.feeRate;
        emit ChangeProtocolFeeRate(_feeRateParams.feeRateType, _feeRateParams.feeRate);
    }

    function _setAverageGasPerAction(AverageGasPerActionParams calldata _averageGasPerActionParams) internal {
        averageGasPerAction[_averageGasPerActionParams.tradingOrderType] = _averageGasPerActionParams
            .averageGasPerAction;
        emit ChangeAverageGasPerAction(
            _averageGasPerActionParams.tradingOrderType,
            _averageGasPerActionParams.averageGasPerAction
        );
    }

    function _setAdditionalGasSpent(uint256 _additionalGasSpent) internal {
        additionalGasSpent = _additionalGasSpent;
        emit ChangeAdditionalGasSpent(_additionalGasSpent);
    }

    function _setPmxDiscountMultiplier(uint256 _pmxDiscountMultiplier) internal {
        pmxDiscountMultiplier = _pmxDiscountMultiplier;
        emit ChangePmxDiscountMultiplier(_pmxDiscountMultiplier);
    }

    function _setGasPriceBuffer(uint256 _gasPriceBuffer) internal {
        gasPriceBuffer = _gasPriceBuffer;
        emit ChangeGasPriceBuffer(_gasPriceBuffer);
    }

    function _setLeverageTolerance(uint256 _leverageTolerance) internal {
        _require(_leverageTolerance <= WadRayMath.WAD / 5, Errors.LEVERAGE_TOLERANCE_IS_NOT_CORRECT.selector); // <= 20%
        leverageTolerance = _leverageTolerance;
        emit ChangeLeverageTolerance(_leverageTolerance);
    }
}

contracts/PrimexDNS/PrimexDNSStorage.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {ERC165Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";

import {IPrimexDNSStorage, IPrimexDNSStorageV2, IPrimexDNSStorageV3} from "./IPrimexDNSStorage.sol";

abstract contract PrimexDNSStorage is IPrimexDNSStorage, ERC165Upgradeable {
    address public override registry;

    /**
     * @notice check the scheme below for additional info
     */
    uint256 public override delistingDelay;
    uint256 public override adminWithdrawalDelay;

    /* solhint-disable max-line-length */
    //                                          =========================================================
    //                                            "delistingDelay" and "adminWithdrawalDelay" explained
    //                                          =========================================================

    //                  * Lenders can’t add liquidity to Bucket.         * Keepers can close all positions opened                * Admin can withdraw all funds from the bucket to Treasury.
    //                  * New positions or orders can’t be opened          through this Bucket.                                  * Admin can withdraw to Treasury all PMX rewards granted to
    //                    in this Bucket.                                * Lenders can withdraw their funds, which became          this bucket for liquidity mining (except already withdrawn amount).
    //                  * Lenders can withdraw existing deposits,          available after all positions were closed by
    //                    traders can close existing positions.            keepers.
    //
    //                |----------------delistingDelay------------------|---------------adminWithdrawalDelay------------------|---------------------------------------------------------------
    //                |                                                |                                                     |
    //      ----------|------------------------------------------------|-----------------------------------------------------|---------------------------------------------------------------
    //                |                                        delistingDeadline                                        adminDeadline
    //      -= bucket is DEPRECATED =-                      -= bucket is DELISTED =-
    //
    //
    /* solhint-enable max-line-length */

    address public override pmx;
    address public override treasury;
    mapping(string => BucketData) public override buckets;
    mapping(string => DexData) public override dexes;
    mapping(uint256 => address) public override cmTypeToAddress;
    address public override dexAdapter;
    address public override aavePool;

    // action to token to fee amount
    mapping(OrderType => mapping(address => uint256)) public override feeRates;

    string[] internal dexesNames;
}

abstract contract PrimexDNSStorageV2 is IPrimexDNSStorageV2, PrimexDNSStorage {
    mapping(OrderType => FeeRestrictions) public override feeRestrictions;
}

abstract contract PrimexDNSStorageV3 is IPrimexDNSStorageV3, PrimexDNSStorageV2 {
    mapping(FeeRateType => uint256) public override protocolFeeRates;
    // Mapping to store average gas per action for different trading order types
    mapping(TradingOrderType => uint256) public override averageGasPerAction;
    // Mapping to store min protocol fee restrictions for different calling method
    mapping(CallingMethod => MinFeeRestrictions) public override minFeeRestrictions;
    // measured in NATIVE_CURRENCY
    uint256 public override maxProtocolFee;
    // additional coefficient to calculate minProtocolFee, measured in wei
    uint256 public override protocolFeeCoefficient;
    // average gas amount spent for a single liquidation, measured in wei
    uint256 public override liquidationGasAmount;
    // gas that will be additionally spend after gasSpent calculation
    uint256 public override additionalGasSpent;
    uint256 public override pmxDiscountMultiplier;
    // protects position from immediate liquidation after gas price changed
    uint256 public override gasPriceBuffer;
    // the limits of available deviation of the actual leverage from the leverage of an order
    uint256 public override leverageTolerance;
}

