// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {ProxyAdmin, ITransparentUpgradeableProxy} from "@openzeppelin/contracts/proxy/transparent/ProxyAdmin.sol";
import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

import "./libraries/Errors.sol";
import {BIG_TIMELOCK_ADMIN} from "./Constants.sol";
import {IPrimexProxyAdmin} from "./interfaces/IPrimexProxyAdmin.sol";

/**
 * @dev This is openzeppelin proxy admin with AccessControl access.
 * Also, there remains the functionality of the Ownable contract
 * for compatibility with the hardhat-deploy plugin,
 * but it does not give privileged access
 */
contract PrimexProxyAdmin is ProxyAdmin, IPrimexProxyAdmin {
    address public immutable registry;

    modifier onlyRole(bytes32 _role) {
        _require(IAccessControl(registry).hasRole(_role, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    constructor(address _registry) {
        _transferOwnership(address(this));
        _require(
            IERC165(_registry).supportsInterface(type(IAccessControl).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        registry = _registry;
    }

    /**
     * @dev Changes the admin of `proxy` to `newAdmin`.
     *
     * Requirements:
     *
     * - This contract must be the current admin of `proxy`.
     */
    function changeProxyAdmin(
        ITransparentUpgradeableProxy proxy,
        address newAdmin
    ) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        proxy.changeAdmin(newAdmin);
    }

    /**
     * @dev Changes the admin of the `beacon` to `newAdmin`.
     *
     * Requirements:
     *
     * - This contract must be the current admin of `proxy`.
     */
    function changeBeaconProxyAdmin(
        UpgradeableBeacon beacon,
        address newAdmin
    ) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        beacon.transferOwnership(newAdmin);
    }

    /**
     * @dev Upgrades `proxy` to `implementation`. See {TransparentUpgradeableProxy-upgradeTo}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgrade(
        ITransparentUpgradeableProxy proxy,
        address implementation
    ) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        proxy.upgradeTo(implementation);
    }

    /**
     * @dev Upgrades `proxy` to `implementation` and calls a function on the new implementation. See
     * {TransparentUpgradeableProxy-upgradeToAndCall}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     */
    function upgradeAndCall(
        ITransparentUpgradeableProxy proxy,
        address implementation,
        bytes memory data
    ) public payable override onlyRole(BIG_TIMELOCK_ADMIN) {
        proxy.upgradeToAndCall{value: msg.value}(implementation, data);
    }

    /**
     * @dev Upgrades the beacon to `implementation`. See {TransparentUpgradeableProxy-upgradeTo}.
     *
     * Requirements:
     *
     * - This contract must be the admin of `proxy`.
     * - implementation must be a contract.
     */

    function upgradeBeacon(
        UpgradeableBeacon beacon,
        address implementation
    ) public override onlyRole(BIG_TIMELOCK_ADMIN) {
        beacon.upgradeTo(implementation);
    }
}

contracts/PrimexRegistry.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

import "./libraries/Errors.sol";

import {BIG_TIMELOCK_ADMIN, MEDIUM_TIMELOCK_ADMIN, SMALL_TIMELOCK_ADMIN, EMERGENCY_ADMIN} from "./Constants.sol";
import {IPrimexRegistry} from "./interfaces/IPrimexRegistry.sol";

contract PrimexRegistry is IPrimexRegistry, AccessControl {
    constructor() {
        _grantRole(BIG_TIMELOCK_ADMIN, msg.sender);
        // MEDIUM_TIMELOCK_ADMIN is admin for SMALL_TIMELOCK_ADMIN
        _setRoleAdmin(SMALL_TIMELOCK_ADMIN, MEDIUM_TIMELOCK_ADMIN);
        // SMALL_TIMELOCK_ADMIN is admin for EMERGENCY_ADMIN
        _setRoleAdmin(EMERGENCY_ADMIN, SMALL_TIMELOCK_ADMIN);
        // BIG_TIMELOCK_ADMIN is admin for other roles
    }

    /**
     * @inheritdoc IPrimexRegistry
     */
    mapping(bytes32 => bool) public override isRoleForContractsOnly;

    function setRoleAdmin(bytes32 role, bytes32 adminRole) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        _setRoleAdmin(role, adminRole);
    }

    /**
     * @inheritdoc IPrimexRegistry
     */
    function setRolesForContractsOnly(bytes32[] calldata roles) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        for (uint256 i; i < roles.length; i++) {
            isRoleForContractsOnly[roles[i]] = true;
        }
    }

    /**
     * @inheritdoc IPrimexRegistry
     */
    function removeRolesForContractsOnly(bytes32[] calldata roles) external override onlyRole(BIG_TIMELOCK_ADMIN) {
        for (uint256 i; i < roles.length; i++) {
            isRoleForContractsOnly[roles[i]] = false;
        }
    }

    function grantRole(bytes32 role, address account) public override(AccessControl, IPrimexRegistry) {
        if (isRoleForContractsOnly[role]) {
            _require(account.code.length > 0, Errors.ADDRESS_IS_NOT_CONTRACT.selector);
        }
        super.grantRole(role, account);
    }
}

contracts/PrimexTimelock.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/governance/TimelockController.sol";

import "./libraries/Errors.sol";

import {GUARDIAN_ADMIN} from "./Constants.sol";
import {IPausable} from "./interfaces/IPausable.sol";

contract PrimexTimelock is IPausable, TimelockController, Pausable {
    address public immutable registry;

    /**
     * @dev Modifier that allows only the Guardian admin to access the function.
     * Throws an error if the sender does not have the GUARDIAN_ADMIN role.
     */
    modifier onlyGuardian() {
        _require(IAccessControl(registry).hasRole(GUARDIAN_ADMIN, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    constructor(
        uint256 _minDelay,
        address[] memory _proposers,
        address[] memory _executors,
        address _admin,
        address _registry
    ) TimelockController(_minDelay, _proposers, _executors, _admin) {
        _require(
            IERC165(_registry).supportsInterface(type(IAccessControl).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        registry = _registry;
    }

    /**
     * @inheritdoc IPausable
     */
    function pause() external override onlyGuardian {
        _pause();
    }

    /**
     * @inheritdoc IPausable
     */
    function unpause() external override onlyGuardian {
        _unpause();
    }

    /**
     * @notice This function overrides a function in the parent contract.
     * @dev The function will revert with the `OPERATION_NOT_SUPPORTED` error.
     */
    function updateDelay(uint256) external pure override {
        _revert(Errors.OPERATION_NOT_SUPPORTED.selector);
    }

    /**
     * @dev Schedules a transaction to be executed in the future.
     * This function can only be called when the contract is not paused.
     * Only callable by the PROPOSER_ROLE role.
     * @param target The address of the contract to be called.
     * @param value The amount of native tokens (in wei) to be sent with the transaction.
     * @param data The data to be passed to the contract's function.
     * @param predecessor The hash of the preceding transaction in the same function.
     * @param salt A random value used as a salt for the scheduled transaction.
     * @param delay The delay, in seconds, before the transaction can be executed.
     */
    function schedule(
        address target,
        uint256 value,
        bytes calldata data,
        bytes32 predecessor,
        bytes32 salt,
        uint256 delay
    ) public override whenNotPaused {
        super.schedule(target, value, data, predecessor, salt, delay);
    }

    /**
     * @dev Schedules a batch of transactions to be executed after a specified delay.
     * This function can only be called when the contract is not paused.
     * Only callable by the PROPOSER_ROLE role.
     * @param targets The array of target addresses for the transactions.
     * @param values The array of values to be sent with each transaction.
     * @param payloads The array of payload data for each transaction.
     * @param predecessor The predecessor hash for the batch of transactions.
     * @param salt The salt value for generating the schedule hash.
     * @param delay The delay in seconds after which the transactions can be executed.
     */
    function scheduleBatch(
        address[] calldata targets,
        uint256[] calldata values,
        bytes[] calldata payloads,
        bytes32 predecessor,
        bytes32 salt,
        uint256 delay
    ) public override whenNotPaused {
        super.scheduleBatch(targets, values, payloads, predecessor, salt, delay);
    }

    /**
     * @notice Cancels a specific task by its ID.
     * @dev This function can only be called by a guardian admin.
     * @param id The ID of the task to be canceled.
     */
    function cancel(bytes32 id) public override {
        bool isGuardian = IAccessControl(registry).hasRole(GUARDIAN_ADMIN, msg.sender);
        if (isGuardian) {
            _grantRole(CANCELLER_ROLE, msg.sender);
        }
        super.cancel(id);
        if (isGuardian) {
            _revokeRole(CANCELLER_ROLE, msg.sender);
        }
    }

    /**
     * @notice Executes a transaction to the specified target with the given parameters.
     * @dev This function can only be called when the contract is not paused.
     * Only callable by the EXECUTOR_ROLE role.
     * @param target The address of the contract to execute the transaction on.
     * @param value The amount of native tokens (in wei) to send along with the transaction.
     * @param payload The data payload to include in the transaction.
     * @param predecessor The predecessor of the transaction (optional).
     * @param salt The salt value to use for the transaction (optional).
     */
    function execute(
        address target,
        uint256 value,
        bytes calldata payload,
        bytes32 predecessor,
        bytes32 salt
    ) public payable override whenNotPaused {
        super.execute(target, value, payload, predecessor, salt);
    }

    /**
     * @notice Executes a batch of transactions.
     * @dev This function can only be called when the contract is not paused.
     * Only callable by the EXECUTOR_ROLE role.
     * @param targets The array of target addresses for the transactions.
     * @param values The array of values to be sent with each transaction.
     * @param payloads The array of payload data for each transaction.
     * @param predecessor The predecessor block's hash.
     * @param salt The salt value for generating a deterministic pseudo-random address.
     */
    function executeBatch(
        address[] calldata targets,
        uint256[] calldata values,
        bytes[] calldata payloads,
        bytes32 predecessor,
        bytes32 salt
    ) public payable override whenNotPaused {
        super.executeBatch(targets, values, payloads, predecessor, salt);
    }
}

contracts/PrimexUpkeep.sol.content:
// (c) 2024 Primex.finance
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.26;

import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {LimitOrderLibrary} from "./libraries/LimitOrderLibrary.sol";
import {PrimexPricingLibrary} from "./libraries/PrimexPricingLibrary.sol";
import {PositionLibrary} from "./libraries/PositionLibrary.sol";
import "./libraries/Errors.sol";

import {BIG_TIMELOCK_ADMIN} from "./Constants.sol";
import {IPositionManagerV2} from "./PositionManager/IPositionManager.sol";
import {ILimitOrderManager} from "./LimitOrderManager/ILimitOrderManager.sol";
import {IPrimexUpkeep} from "./interfaces/IPrimexUpkeep.sol";
import {IBestDexLens} from "./interfaces/IBestDexLens.sol";
import {IPrimexLens} from "./interfaces/IPrimexLens.sol";
import {ILimitPriceCOM} from "./interfaces/ILimitPriceCOM.sol";
import {ITakeProfitStopLossCCM} from "./interfaces/ITakeProfitStopLossCCM.sol";

contract PrimexUpkeep is IPrimexUpkeep, Initializable {
    uint256 public constant MAX_UINT256 = type(uint256).max;

    address public immutable override registry;
    IPositionManagerV2 public override pm;
    ILimitOrderManager public override lom;
    IPrimexLens public override primexLens;
    IBestDexLens public override bestDexLens;

    /**
     * @dev Modifier that checks if the caller has a specific role.
     * @param _role The role identifier to check.
     */
    modifier onlyRole(bytes32 _role) {
        _require(IAccessControl(registry).hasRole(_role, msg.sender), Errors.FORBIDDEN.selector);
        _;
    }

    constructor(address _registry) {
        _require(
            IERC165(_registry).supportsInterface(type(IAccessControl).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        registry = _registry;
    }

    function initialize(
        address _positionManager,
        address _limitOrderManager,
        address _bestDexLens,
        address _primexLens
    ) external override initializer onlyRole(BIG_TIMELOCK_ADMIN) {
        _require(
            IERC165(_bestDexLens).supportsInterface(type(IBestDexLens).interfaceId) &&
                IERC165(_positionManager).supportsInterface(type(IPositionManagerV2).interfaceId) &&
                IERC165(_primexLens).supportsInterface(type(IPrimexLens).interfaceId) &&
                IERC165(_limitOrderManager).supportsInterface(type(ILimitOrderManager).interfaceId),
            Errors.ADDRESS_NOT_SUPPORTED.selector
        );
        pm = IPositionManagerV2(_positionManager);
        lom = ILimitOrderManager(_limitOrderManager);
        bestDexLens = IBestDexLens(_bestDexLens);
        primexLens = IPrimexLens(_primexLens);
    }

    /**
     * @inheritdoc IPrimexUpkeep
     */
    function performUpkeepOrders(OpenByOrderInfo[] calldata toOpenByOrder, address keeper) external payable override {
        for (uint256 i; i < toOpenByOrder.length; i++) {
            try
                lom.openPositionByOrder{value: toOpenByOrder[i].value}(
                    LimitOrderLibrary.OpenPositionParams({
                        orderId: toOpenByOrder[i].id,
                        conditionIndex: toOpenByOrder[i].conditionIndex,
                        comAdditionalParams: toOpenByOrder[i].comAdditionalParams,
                        firstAssetMegaRoutes: toOpenByOrder[i].firstAssetMegaRoutes,
                        depositInThirdAssetMegaRoutes: toOpenByOrder[i].depositInThirdAssetMegaRoutes,
                        keeper: keeper,
                        firstAssetOracleData: toOpenByOrder[i].firstAssetOracleData,
                        thirdAssetOracleData: toOpenByOrder[i].thirdAssetOracleData,
                        depositSoldAssetOracleData: toOpenByOrder[i].depositSoldAssetOracleData,
                        nativePmxOracleData: toOpenByOrder[i].nativePmxOracleData,
                        positionNativeAssetOracleData: toOpenByOrder[i].positionNativeAssetOracleData,
                        nativePositionAssetOracleData: toOpenByOrder[i].nativePositionAssetOracleData,
                        pmxPositionAssetOracleData: toOpenByOrder[i].pmxPositionAssetOracleData,
                        positionUsdOracleData: toOpenByOrder[i].positionUsdOracleData,
                        nativeSoldAssetOracleData: toOpenByOrder[i].nativeSoldAssetOracleData,
                        pullOracleData: toOpenByOrder[i].pullOracleData,
                        pullOracleTypes: toOpenByOrder[i].pullOracleTypes,
                        borrowedAmount: toOpenByOrder[i].borrowedAmount
                    })
                )
            {} catch Error(string memory revertReason) {
                emit ErrorHandled(toOpenByOrder[i].id, keeper, revertReason);
            } catch Panic(uint revertReason) {
                emit PanicErrorHandled(revertReason);
            } catch (bytes memory revertReason) {
                emit LowLevelErrorHandled(revertReason);
            }
        }
    }

    /**
     * @inheritdoc IPrimexUpkeep
     */
    function performUpkeepOrdersUnsafe(
        OpenByOrderInfo[] calldata toOpenByOrder,
        address keeper
    ) external payable override {
        for (uint256 i; i < toOpenByOrder.length; i++) {
            lom.openPositionByOrder{value: toOpenByOrder[i].value}(
                LimitOrderLibrary.OpenPositionParams({
                    orderId: toOpenByOrder[i].id,
                    conditionIndex: toOpenByOrder[i].conditionIndex,
                    comAdditionalParams: toOpenByOrder[i].comAdditionalParams,
                    firstAssetMegaRoutes: toOpenByOrder[i].firstAssetMegaRoutes,
                    depositInThirdAssetMegaRoutes: toOpenByOrder[i].depositInThirdAssetMegaRoutes,
                    keeper: keeper,
                    firstAssetOracleData: toOpenByOrder[i].firstAssetOracleData,
                    thirdAssetOracleData: toOpenByOrder[i].thirdAssetOracleData,
                    depositSoldAssetOracleData: toOpenByOrder[i].depositSoldAssetOracleData,
                    nativePmxOracleData: toOpenByOrder[i].nativePmxOracleData,
                    positionNativeAssetOracleData: toOpenByOrder[i].positionNativeAssetOracleData,
                    nativePositionAssetOracleData: toOpenByOrder[i].nativePositionAssetOracleData,
                    pmxPositionAssetOracleData: toOpenByOrder[i].pmxPositionAssetOracleData,
                    positionUsdOracleData: toOpenByOrder[i].positionUsdOracleData,
                    nativeSoldAssetOracleData: toOpenByOrder[i].nativeSoldAssetOracleData,
                    pullOracleData: toOpenByOrder[i].pullOracleData,
                    pullOracleTypes: toOpenByOrder[i].pullOracleTypes,
                    borrowedAmount: toOpenByOrder[i].borrowedAmount
                })
            );
        }
    }

    /**
     * @inheritdoc IPrimexUpkeep
     */
    function performUpkeepPositions(
        ClosePositionInfo[] calldata toLiquidate,
        address keeper
    ) external payable override {
        for (uint256 i; i < toLiquidate.length; i++) {
            try
                pm.closePositionByCondition{value: toLiquidate[i].value}(
                    IPositionManagerV2.ClosePositionByConditionParams({
                        id: toLiquidate[i].id,
                        keeper: keeper,
                        megaRoutes: toLiquidate[i].positionAssetMegaRoutes,
                        conditionIndex: toLiquidate[i].conditionIndex,
                        ccmAdditionalParams: toLiquidate[i].ccmAdditionalParams,
                        closeReason: toLiquidate[i].closeReason,
                        positionSoldAssetOracleData: toLiquidate[i].positionSoldAssetOracleData,
                        nativePmxOracleData: toLiquidate[i].nativePmxOracleData,
                        positionNativeAssetOracleData: toLiquidate[i].positionNativeAssetOracleData,
                        pmxSoldAssetOracleData: toLiquidate[i].pmxSoldAssetOracleData,
                        nativeSoldAssetOracleData: toLiquidate[i].nativeSoldAssetOracleData,
                        pullOracleData: toLiquidate[i].pullOracleData,
                        pullOracleTypes: toLiquidate[i].pullOracleTypes
                    })
                )
            {} catch Error(string memory revertReason) {
                emit ErrorHandled(toLiquidate[i].id, keeper, revertReason);
            } catch Panic(uint revertReason) {
                emit PanicErrorHandled(revertReason);
            } catch (bytes memory revertReason) {
                emit LowLevelErrorHandled(revertReason);
            }
        }
    }

    /**
     * @inheritdoc IPrimexUpkeep
     */
    function performUpkeepPositionsUnsafe(
        ClosePositionInfo[] calldata toLiquidate,
        address keeper
    ) external payable override {
        for (uint256 i; i < toLiquidate.length; i++) {
            IPositionManagerV2.ClosePositionByConditionParams memory params = _getClosePositionByConditionParams(
                toLiquidate[i],
                keeper
            );
            pm.closePositionByCondition{value: toLiquidate[i].value}(params);
        }
    }

    function _getClosePositionByConditionParams(
        ClosePositionInfo calldata info,
        address keeper
    ) internal pure returns (IPositionManagerV2.ClosePositionByConditionParams memory) {
        return
            IPositionManagerV2.ClosePositionByConditionParams({
                id: info.id,
                keeper: keeper,
                megaRoutes: info.positionAssetMegaRoutes,
                conditionIndex: info.conditionIndex,
                ccmAdditionalParams: info.ccmAdditionalParams,
                closeReason: info.closeReason,
                positionSoldAssetOracleData: info.positionSoldAssetOracleData,
                nativePmxOracleData: info.nativePmxOracleData,
                positionNativeAssetOracleData: info.positionNativeAssetOracleData,
                pmxSoldAssetOracleData: info.pmxSoldAssetOracleData,
                nativeSoldAssetOracleData: info.nativeSoldAssetOracleData,
                pullOracleData: info.pullOracleData,
                pullOracleTypes: info.pullOracleTypes
            });
    }
}

